/********************************************************************************
* 기      능   : 조회
********************************************************************************/
function fn_query() {

	if( gfn_getDayCnt(ds_days,cal_i_credt1.value, cal_i_credt2.value) > 365)
	{
		gfn_showAlert("CW00078","12");
		cal_i_credt1.SetFocus();
		return;
	}

	if( gfn_getDayCnt(ds_days,cal_i_appdt1.value, cal_i_appdt2.value) > 365)
	{
		gfn_showAlert("CW00078","12");
		cal_i_credt1.SetFocus();
		return;
	}	

	if( gfn_getDayCnt(ds_days,cal_i_sdate1.value, cal_i_sdate2.value) > 365)
	{
		gfn_showAlert("CW00078","12");
		cal_i_credt1.SetFocus();
		return;
	}		
	// 필수 입력사항 및 일자 체크 
	if (length(ed_i_posid.value) == 0 && length(ed_i_jqprnum.value) == 0 && length(ed_i_jqprno.value) == 0)
	{
		if(gfn_getDayCnt(ds_days,cal_i_credt1.value, cal_i_credt2.value) < 0 &&
		   gfn_getDayCnt(ds_days,cal_i_appdt1.value, cal_i_appdt2.value) < 0 &&
		   gfn_getDayCnt(ds_days,cal_i_sdate1.value, cal_i_sdate2.value) < 0)
		{
			gfn_showMsg("CW00002");
			return;
		}		   
	}

	// sort mark 삭제 
	gfn_clearGridSortMark(grd_list);
	//ds_list.row = -1;

	//http.TimeOut = 1800;

	// 조회 처리 
	tit_callService(
        "QM0601002A_ACT"
        , "query"
        , "ds_cond=ds_cond"
        , "ds_list=ds_list  ds_error=ds_error"
        , ""
        , "fn_afterQuery"
        , false);
}



