/********************************************************************************
* 기      능   : 조회 Callback  
********************************************************************************/
function fn_afterQuery(errCode, errMsg) {
	// 메시지 처리 

    //trace(ds_list.SaveXML());
	if(ds_error.GetRowCount() > 0) {
		alert(ds_error.GetColumn(0, "ERRMSG"));
		return;
	} else {
		gfn_showSearchMsg(grd_list);
	}	
}

