/********************************************************************************
* 기      능   : 그리드 헤더 클릭시 정렬 처리 
********************************************************************************/
function grd_list_OnHeadClick(obj,nCell,nX,nY,nPivotIndex)
{
	if ( nCell == 0 ) 
	{
		gfn_selectGridCol(obj);
	} 
	else if ( nCell > 1 ) 
	{
		gfn_sortGrid(obj, nCell);
	}
}






function btn_pjt_OnClick(obj)
{
	var arg = "pjt="+quote(ed_posid.value);
	var pjt= null;
	pjt = gfn_openPopup("CS9601002A",true,arg);

    if (pjt != null && obj == btn_posid) {
		ed_posid.value = pjt[0];
    }
}

function btn_direct_into(obj)
{

    ds_list_1.ClearData();
    gfn_copyDs(ds_list, ds_list_1, "CHECK=1");

    //trace(ds_list_1.SaveXML());
	// 처리 데이터 확인
	if (ds_list_1.rowcount < 1) {
		gfn_showAlert("CW00013");
		return;
	}

   	for (var i = 0; i < ds_list_1.rowcount ; i++)
   	{
		if (length(ds_list_1.GetColumn(i, "JQPRNO")) < 1)
		{
			gfn_showalert("JQPR번호가 없으면 저장할 수 없습니다.");
			return;
		}
   	}

	if (!gfn_showConfirm("CI00008"))
	 {
		return;
	 }	

	tit_clearActionInfo();

    tit_addSelectExActionInfo("qm06:QM0601001A_S1","qm06:QM0601001A_I1","qm06:QM0601001A_U1");	

	tit_callService(
		""
		, ""
		, "ds_list_1=ds_list_1:A"
		, ""
		, ""
		, "fn_afterSave");

}

