function btn_print_OnClick(obj)
{

    ds_head.ClearData();
    gfn_copyDs(ds_list, ds_head, "CHECK2=1");
	// 처리 데이터 확인
	if (ds_head.rowcount > 1)
	{
		alert("하나의 JQPR관리번호만 선택가능합니다.");
		return;
	}
    else if (ds_head.rowcount == 0)
    {
		alert("JQPR관리번호를 선택하십시요.");
		return;    
	}

	gfn_copyDs(ds_list, ds_list_1, "JQPRNUM="+quote(ds_head.GetColumn(0, "JQPRNUM")));

	ds_cond_2.SetColumn(0, "POSID", substr(ds_head.GetColumn(0, "JQPRNO"),1,9));

	tit_clearActionInfo();
	tit_addSearchActionInfo("qm06:QM0601002A_S2", null, true);

	tit_callService(
		""
		, ""
		, "ds_cond_2=ds_cond_2"
		, "ds_cellp=ds_cellp"
		, ""
		, "fn_afterQuery2"
		, true);	
}

