	// arg += " fa_usd=" + quote(ds_head.GetColumn(0, "USD"));
	// arg += " fa_umr=" + quote(ds_head.GetColumn(0, "UMR"));
	// arg += " fa_uhj=" + quote(ds_head.GetColumn(0, "UHJ"));
	// arg += " fa_year=" + quote(ds_head.GetColumn(0, "YEAR"));


   	for (var i = 0; i < ds_list_1.rowcount ; i++)
