// [ PART 4]
// Callback 함수 정의 : callback 함수명은 메인 함수의 fn_xxxx() 에서 xxxx 부분은 fn_afterXXXX()
// 형태가 되도록 작성 
//=========================================================================================
/********************************************************************************
* 기      능   : 조회 Callback  
********************************************************************************/
function fn_afterQuery(errCode, errMsg) {
	// 메시지 처리 
trace(ds_list.SaveXML());
    //trace(ds_list.SaveXML());
	if(ds_error.GetRowCount() > 0) {
		alert(ds_error.GetColumn(0, "ERRMSG"));
		return;
	} else {
		gfn_showSearchMsg(grd_list);
	}		

	if ( G_VEND_CODE == "0000009913" )
	{
		btn_txt.Enable = false;
//		btn_ektel.Enable = false;
		btn_arrival.Enable = false;
		btn_ipgo_date.Enable = false;
		btn_jjw_date.Enable = false;
		btn_send_date.Enable = false;
	}
}

