/********************************************************************************
* 기      능   : 그리드 헤더 클릭시 정렬 처리 
********************************************************************************/
function grd_list_OnHeadClick(obj,nCell,nX,nY,nPivotIndex)
{
	if ( nCell == 0 ) 
	{
		//fn_selectGridCol(obj);
		var grdNm = "";

		if ( type( Obj ) == "STRING") {
			grdNm = Obj;
		} else {
			grdNm = Obj.id;
		}
		var mak = "N";

		gfn_openPopupUrl("QM06::QM0601004B.xml", true, "fa_gridName='" + grdNm + "' fa_isMake='" + mak + "'" );
	} 
	else if ( nCell > 1 ) 
	{
		gfn_sortGrid(obj, nCell);
	}
}


function btn_pjt_OnClick(obj)
{
	var arg = "pjt="+quote(ed_posid.value);
	var pjt= null;
	pjt = gfn_openPopup("CS9601002A",true,arg);

    if (pjt != null && obj == btn_posid) {
		ed_posid.value = pjt[0];
    }
}

function btn_direct_into(obj)
{
    ds_list_1.ClearData();
    gfn_copyDs(ds_list, ds_list_1, "CHECK=1");
	// 처리 데이터 확인
	if (ds_list_1.rowcount < 1) {
		gfn_showAlert("CW00013");
		return;
	}

   	for (var i = 0; i < ds_list_1.rowcount ; i++)
   	{
		if (length(ds_list_1.GetColumn(i, "JQPRNO")) < 1)
		{
			gfn_showalert("JQPR번호가 없으면 저장할 수 없습니다.");
			return;
		}if (G_VEND_CODE != "0000009920" && ds_list_1.GetColumn(i, "IPGO_1000") > ds_list_1.GetColumn(i, "IPGO_8000")){
			gfn_alert("입력하신 입고예정일은 발주된 납기일보다 경과된 일자로" +
			"\n가장 빠르게 입고 될 수 있는 입고예정일로 다시 재 검토 후 입력 바랍니다." +
			"\nJQPR번호 : " + ds_list_1.GetColumn(i, "JQPRNO") + ", 자재번호 : " + ds_list_1.GetColumn(i, "MATNR") +
			"\n입고예정일 : " + ds_list_1.GetColumn(i, "IPGO_1000") + ", 납기일 : " + ds_list_1.GetColumn(i, "IPGO_8000"));
			return;
		} // 2019.12.05 JYJ JQPR 입고예정일, 납기일 비교 후 입고예정일이 더 늦는 경우 팝업 추가
	}

	if (!gfn_showConfirm("CI00008"))
	 {
		return;
	 }	

	tit_clearActionInfo();

    tit_addSelectExActionInfo("qm06:QM0601001A_S1","qm06:QM0601001A_I4","qm06:QM0601001A_U4");	

	tit_callService(
		""
		, ""
		, "ds_list_1=ds_list_1:A"
		, ""
		, ""
		, "fn_afterSave");

}

