			gfn_showalert("JQPR번호가 없으면 저장할 수 없습니다.");
			return;
		}
   	}	

	if (!gfn_showConfirm("CI00008"))
	 {
		return;
	 }


	tit_clearActionInfo();

    tit_addSelectExActionInfo("qm06:QM0601001A_S1" , "qm06:QM0601001A_I6", "qm06:QM0601001A_U6");	

	tit_callService(
		""
		, ""
		, "ds_list_1=ds_list_1:A"
		, ""
		, ""
		, "fn_afterSave");	
}

