function fn_afterSave(errCode, errMsg){
	if ( errCode != 0 ) {
		gfn_showMsg("CE00001");
	} else {
		gfn_showMsg("CI00003");
		fn_query();
	}
}


/********************************************************************************
* 기      능   : 그리드에서 날짜 입력시 
********************************************************************************/
    var col = "";
function grd_list_OnExpandEdit(obj,nRow,nCell,strVal,nPivotIndex)
{	
	var rect = obj.GetCellRect(nRow, ncell);
	cal_temp.top = rect[1] +100 ;
	cal_temp.Left = rect[0]; 
	cal_temp.Visible = true;

	if (nCell = 19)
	{
		cal_temp.value = ds_list.getColumn(nRow, "SBUDAT1");	
		col = "SBUDAT1";
	}
	else if (nCell = 23)    
	{
		cal_temp.value = ds_list.getColumn(nRow, "SBUDAT2");	
		col = "SBUDAT2";
	}
	else if (nCell = 22)
	{
		cal_temp.value = ds_list.getColumn(nRow, "TB_SDAT");	
		col = "TB_SDAT";
	}
	else if (nCell = 44)
	{
		cal_temp.value = ds_list.getColumn(nRow, "IPGO_1000");	
		col = "IPGO_1000";
	}
	else if (nCell = 34)
	{
		cal_temp.value = ds_list.getColumn(nRow, "SU_SDAT");	
		col = "SU_SDAT";
	}
	else if (nCell = 43)
	{
		cal_temp.value = ds_list.getColumn(nRow, "SU_IDAT");	
		col = "SU_IDAT";
	}
	cal_temp.SetFocus();
}


function cal_temp_OnDayClick(obj,strText)
{

	gfn_clearMsgBar();

	ds_list.setColumn(ds_list.row, col, strText);

	col = "";
	obj.Visible = false;

}

function cb_atype_OnChanged(obj)
{

	if (obj.value == "1")
	{
		ds_mat_code.Filter(" CODE < 200 ");
		cb_mat_code.index = 0;
	} else if (obj.value == "2")
	{
		ds_mat_code.Filter(" CODE > 199 || CODE== '' ");
		cb_mat_code.index = 0;
	} else
	{
		ds_mat_code.UnFilter();
		cb_mat_code.index = 0;
	}

}

/********************************************************************************
* 기      능   : 달력에서 포커스 잃었을 경우 
********************************************************************************/
function cal_temp_OnKillFocus(obj)
{
	obj.Visible = false;
	col = "";
}

