function grd_list_OnCellDBLClick(obj,nRow,nCell,nX,nY,nPivotIndex)
{
	var arg = "fa_posid_1="+quote( substr(ds_list.GetColumn(nRow,"JQPRNO"),1,9));
	if(nCell == '2' || nCell == '3' || nCell == '4' || nCell == '5') {
		gfn_openPopup("WB0199071A",true, arg);
	}

	// 품목 기타정보 (발주업체) 조회 팝업
	if (nCell==31) // name1(발주업체)
	{ 
		var arg = "fa_lifnr=" + quote(ds_list.GetColumn(nRow,"BAL_LIFNR"));

		gfn_openPopup("MM0102002D", true, arg, 420, 529);
	} 
	if(nCell == '11' || nCell == '12') {
		var arg = "fa_jqprnum=" + quote(ds_list.GetColumn(nRow,"JQPRNUM"));

		gfn_openPopup("QM0601004C",true, arg);
	}
	if(nCell == '30') {
		var arg = "fa_ektel=" + quote(ds_list.GetColumn(nRow,"EKTEL"));

		gfn_openPopup("WB0199091A",true, arg);
	}
}


/********************************************************************************
* 기      능   : 업체 찾기 팝업
********************************************************************************/
function grd_list_OnCellClick(obj,nRow,nCell,nX,nY,nPivotIndex) {

	if(nCell == 35) {  // JQPR_LIFNR(수리업체)
		var arg = "comName="+quote(ds_list.GetColumn(nRow, "JQPR_LIFNR_NM"));
		var rtn = gfn_openPopup("WB0101001C", true, arg);

		if(rtn != null) {
			ds_list.SetColumn(nRow, "JQPR_LIFNR", rtn[0]);
			ds_list.SetColumn(nRow, "JQPR_LIFNR_NM", rtn[1]);
		}
	}
}


function ufn_invno_in(obj)
{
	ds_list_1.ClearData();
    gfn_copyDs(ds_list, ds_list_1, "CHECK=1");

	// 처리 데이터 확인
	if (ds_list_1.rowcount < 1) {
		gfn_showAlert("CW00013");
		return;
	}

   	for (var i = 0; i < ds_list_1.rowcount ; i++)
   	{
		if (length(ds_list_1.GetColumn(i, "JQPRNO")) < 1)
		{
			gfn_showalert("JQPR번호가 없으면 저장할 수 없습니다.");
			return;
		}
   	}	

	if (!gfn_showConfirm("CI00008"))
	 {
		return;
	 }


	tit_clearActionInfo();

    tit_addSelectExActionInfo("qm06:QM0601001A_S1" , "qm06:QM0601001A_I7", "qm06:QM0601001A_U7");	

	tit_callService(
		""
		, ""
		, "ds_list_1=ds_list_1:A"
		, ""
		, ""
		, "fn_afterSave");	
}

