function grd_list_OnCellDblClick(obj,nRow,nCell,nX,nY,nPivotIndex)
{

	// 증빙자료 첨부
	if(nCell == '39') {	

		var formID="";

		formID = "QM0701001E";  

		var sDept     = ds_list.GetColumn(nRow, "DEPT");
		var sGjahr     = ds_list.GetColumn(nRow, "GJAHR");
		var sItem     = ds_list.GetColumn(nRow, "ITEM");
		var sItemnm     = ds_list.GetColumn(nRow, "ITEMNM");

		var arg = "DEPT="+quote(sDept);
			arg += " GJAHR="+quote(sGjahr);
			arg += " ITEM="+quote(sItem);
			arg += " ITEMNM="+quote(sItemnm);

		gfn_openPopup(formID, true, arg);		
	}

}
