var _F_FILE_DIR = "";	// 로컬에서 현재 선택한 파일 디렉토리 
var _F_FILE_NAME = "";	// 로컬에서 현재 선택한 파일  명 
var _F_FILE_SIZE = 0;	// 파일 사이즈 
var _F_FILE_DELETE = false;
var F_SIZE = 1024 * 10096;	// 4096K
var F_INIT_CNT = 0;
var F_CLOSE_CNT = 0;
function fn_OnLoadCompleted(obj)
{

    gfn_initForm();
	gfn_initDs(ds_cond, true);	

