	ds_cond.SetColumn(0,"ITEMNM", ITEMNM);

	// #첨부파일 조회
	ufn_selectFile();

	//parent.close();
}

/********************************************************************************
* 기      능   : 닫기  
********************************************************************************/
function fn_close() {
	F_CLOSE_CNT = ds_file.GetRowCount();

	if(F_INIT_CNT != F_CLOSE_CNT)
	{
		parent.fn_query();
	}

	close();
}

