#include "LIB::commons.js";	// 공통 스크립트 
//=========================================================================================
// [ PART 1 ]
// Form에서 사용될 전역변수를 선언
// FORM에서 사용되는 전역변수는 대문자로 작성하며, F_XXX 의 형태를 갖도록 한다. 
//=========================================================================================


var F_ADMIN_DEPT = "F2,F6,D6,E2,10";	// 효율개선부 -> 총무부로 변경(조직개편2016.04), 정보기술실(관리부서)		
var F_ADMIN_YN   = "Y";		// 관리부서 여부

//=========================================================================================
// [ PART 2 ]
// Form Loading / Unloading 시 작업 정의
//=========================================================================================
function fn_OnLoadCompleted(obj) {

	gfn_initForm();

	// 넘어온 param 처리
	var curYear = parseInt(substr(gfn_getCurrDate(),0,4));
	fn_makeYear(curYear,-2,1);

	if (isExistVar("P_GJAHR", true))	{
		cb_gjahr.Value = P_GJAHR;
	}

	// 원가절감 부서정보 조회
	var codeArr    = ["QM01"];
	var dsObjArr   = [ds_dept_cd];
	var addTypeArr = [G_CODE_NONE];
	gfn_searchCommCode(codeArr, dsObjArr, addTypeArr);

}	
/********************************************************************************
* 기      능   : form UnloadCompleted 
********************************************************************************/
function fn_OnUnloadCompleted(obj) {
	return gfn_isFormUnload(obj);
}

/********************************************************************************
* 기      능   : 공통코드가 조회된 후의 Callback 함수 
********************************************************************************/
function fn_completeInit(sCode, sDsObj, sCodeType) {
	cb_dept.Value = G_DPT1;
    cb_item.Value = P_ITEM;

	// 관리부서가 아닌경우 부서정보 변경 불가
	if (IndexOf(F_ADMIN_DEPT, G_DPT1) == -1)		{
		F_ADMIN_YN = "N";
	}

	// 추후 정보 확인 후 삭제 필요 &&&&
	if (length(trim(G_DPT1)) == 0)	{
		F_ADMIN_YN = "Y";
	}
	// 추후 정보 확인 후 삭제 필요 &&&&

	// 부서정보 활성화
	if (F_ADMIN_YN == "Y")		{
		cb_dept.Enable = false;

		if (isExistVar("P_DEPT", true))	{
			cb_dept.Value = P_DEPT;
		}
	}else	{
		cb_dept.Enable      = false;
	}

	 if(cb_dept.Value == 'CK')
    {
      btn_delete.Enable = false;
      btn_save.Enable = false;
    }




	// 항목정보 조회
	// 항목정보 조회
	tit_clearActionInfo();
	tit_addSearchActionInfo("qm07:QM0701002A_S1");
	tit_addSearchActionInfo("qm07:QM0701002A_S5");
	tit_addSearchActionInfo("qm07:QM0701002A_S4");
	tit_callService(
          ""
        , ""
        , "ds_cond=ds_cond"
        , "ds_item_cd=ds_item_cd ds_matnr_cd=ds_matnr_cd ds_code=ds_code"
        , ""
        , "fn_afterItemQuery"
        , true);

}

/********************************************************************************
* 기      능   : 항목(내역)정보 조회 (최초 화면 로딩 시에만 적용)
********************************************************************************/
function fn_afterItemQuery(errCode, errMsg) {

	// 자재 등록에서 호출된 경우 (최초 화면 로딩 시에만 적용)
	if (isExistVar("P_ITEM", true))	{
		cb_item.Value = P_ITEM;
     	fn_afterMatnr();
	}
}
/********************************************************************************
* 기      능   : 자재(내역)정보 조회 (최초 화면 로딩 시에만 적용)
********************************************************************************/
function fn_afterMatnr(errCode, errMsg) {

	// 자재  항목등록에서 호출된 경우 (최초 화면 로딩 시에만 적용)
	if (isExistVar("P_MATNR", true))	{
		cb_matnr.Value = P_MATNR;
//     	fn_afterMantnr();
        fn_query();
	}
}
