/********************************************************************************
* 기      능   : 조회 
********************************************************************************/
function fn_query() {

     btn_pdmcase.Enable = false;
	//년도필드 필수체크	
	if(gfn_isNull(cb_gjahr, true, st_gjahr.value))
		return;

	if(gfn_isNull(cb_dept, true, st_dept.value))
		return;

	if(gfn_isNull(cb_item, true, st_item.value))
		return;

	// HTTP SESSION TIMEOUT을 변경
	//http.TimeOut = 1800;

	tit_clearActionInfo();
	tit_addSearchActionInfo("qm07:QM0701002B_S1");

	tit_callService(
          ""
        , ""
        , "ds_cond=ds_cond"
        , "ds_list=ds_list"
        , ""
        , "fn_afterQuery"
        , true);
}

/********************************************************************************
* 기      능   : 닫기  
