function fn_close() {
	close();
}

/********************************************************************************
* 기      능   : 저장  
********************************************************************************/
function fn_save()
{
	// 입력정보 확인
	if (fn_check())		{
		if (!gfn_showConfirm("CI00008"))	{
			return;
		}

		ds_list.SetColumn(0, "FLAG",   "U");
		ds_list.SetColumn(0, "ADDGBN", "R");
		ds_list.SetColumn(0, "MATNR", cb_matnr.Text);
       // alert(ds_list.GetColumn(0,"MATNR"));
		var vSpec12 = trim(ds_list.GetColumn(0, "SPEC12"));

		var vEtm = trim(ds_list.GetColumn(0, "ETM"));
		if (length(vSpec12) > 0)	{
			for (var i = length(vSpec12);  i > 0; i--)	{
				if(right(vSpec12, 1) == ",")	{
					vSpec12 = trim(substr(vSpec12, 0, length(vSpec12) - 1));
				}else	{
					break;
				}
			}
			// SQL오류 방지 처리 (마지막에 , 포함)
			ds_list.SetColumn(0, "SPEC12", vSpec12);
		}

        if (length(vEtm) > 0)	{
			for (var i = length(vEtm);  i > 0; i--)	{
				if(right(vEtm, 1) == ",")	{
					vEtm = trim(substr(vEtm, 0, length(vEtm) - 1));
				}else	{
					break;
				}
			}
			// SQL오류 방지 처리 (마지막에 , 포함)
			ds_list.SetColumn(0, "ETM", vEtm);

		}

		tit_clearActionInfo();
		tit_addSaveActionInfo(
			  ""
			, "qm07:QM0701002B_U1"
			, ""
			, "FLAG");

		tit_callService(
			  ""
			, ""
			, "ds_list=ds_list"
			, ""
			, ""
			, "fn_afterSave");
	}
}

