//=========================================================================================
// [ PART 2 ]
// Form Loading / Unloading 시 작업 정의
//=========================================================================================
function fn_OnLoadCompleted(obj) {

	// Form 로드 이벤트 맨 첫줄에 기술 
	gfn_initForm(true);

	cal_rsltym.Value = gfn_getCurrDate();
	cal_rsltym.SetFocus();

	// 관리부서가 아닌경우 부서정보 변경 불가
	if (IndexOf(F_ADMIN_DEPT, G_DPT1) == -1)		{
		F_ADMIN_YN = "N";
	}

	// 추후 정보 확인 후 삭제 필요 &&&&
	if (length(trim(G_DPT1)) == 0)	{
		F_ADMIN_YN = "Y";
	}
	if (F_ADMIN_YN == "Y")	{
		cb_dept.Enable = true;
	}else	{
		cb_dept.Enable = true;
		//cb_dept.Enable = false;
	}

	// 원가절감실적집계에서 호출된 경우 처리
	if (isExistVar("P_RSLTYM", true))	{
		cal_rsltym.Value = P_RSLTYM;
		F_CALLYN = "Y";
	}

	if (isExistVar("P_DEPT", true))	{
		cb_dept.Value = P_DEPT;
		F_CALLYN = "Y";
	}
    //h1 프로젝트 담당자 조회권한 추가  + 이택규 bj 김종진cj 서창수 cj 추가 
	if(G_USER_ID == '2007665' or G_USER_ID == '2015960'or G_USER_ID == '2019841'or  G_USER_ID == '2023946'or  G_USER_ID == '2012707'or  G_USER_ID == '2016648')
    {
        btn_save.Enable = true;
        cb_dept.Enable      = true;
		cb_dept.Value       = "";
    }

	if(F_ADMIN_YN == "Y")
	{
		// 원가절감 부서정보 조회
		var codeArr    = ["QM01"];
		var dsObjArr   = [ds_dept_cd];
		var addTypeArr = [G_CODE_NONE];
		var oderTypeArr = ["CTEXT2"];
		gfn_searchCommCode2(codeArr, dsObjArr, addTypeArr, oderTypeArr);

	}
	else
	{
		// 로그인 사용자별 권한에 따른 부서정보 조회
		tit_clearActionInfo();
		tit_addSearchActionInfo("qm07:QM0701001A_S4");

		tit_callService(
			  ""
			, ""
			, "ds_cond=ds_cond"
			, "ds_dept_cd=ds_dept_cd"
			, ""
			, "fn_completeInit"
			, true);
	}


   //gfn_alert("금일 조직개편작업으로 사용불가합니다.");  
    //   fn_close();
	 //  return; 




}

/********************************************************************************
