}

/********************************************************************************
* 기      능   : 조회 (header 조회)
********************************************************************************/
function fn_query() {

	gfn_showMsg("");

	//년도필드 필수체크	
	if(gfn_isNull(cal_rsltym, true, st_rsltym.value))
		return;

	// 기준년도 setting
	ds_cond.SetColumn(0, "GJAHR", SubStr(cal_rsltym.Value, 0, 4));

	// 실적년월 setting
	ds_cond.SetColumn(0, "RSLTYM", SubStr(cal_rsltym.Value, 0, 6));

	if(gfn_isNull(cb_dept, true, st_dept.value))
		return;

	ed_confyn.Text = "N";

	// HTTP SESSION TIMEOUT을 변경
	//http.TimeOut = 1800;

	tit_clearActionInfo();
	tit_addSearchActionInfo("qm07:QM0702002A_S1");

	tit_callService(
          ""
        , ""
        , "ds_cond=ds_cond"
        , "ds_list=ds_list"
        , ""
        , "fn_afterQuery"
        , true);
}

/********************************************************************************
* 기      능   : 조회 Callback  
********************************************************************************/
function fn_afterQuery(errCode, errMsg) {
	gfn_showSearchMsg(grd_list, errCode, errMsg);
	//gfn_showMsg("CI00002",ds_list.RowCount()+"");

	// 확정여부 조회
	tit_clearActionInfo();
	tit_addSearchActionInfo("qm07:QM0702002A_S2");

	tit_callService(
          ""
        , ""
        , "ds_cond=ds_cond"
        , "ds_conf=ds_list"
        , ""
        , "fn_afterConfYNQuery"
        , true);
}

/********************************************************************************
* 기      능   : 확정여부 조회 Callback  
********************************************************************************/
function fn_afterConfYNQuery(errCode, errMsg) {

	// 확정 정보를 확인하여 처리
	var vConfDate = ds_conf.GetColumn(0, "CFDATE");
	if (ds_conf.GetRowCount() > 0)	{
		if(vConfDate == null || replace(vConfDate, "00000000", "") == "")	{
			ed_confyn.Text = "N";
		}else	{
			ed_confyn.Text = "Y";
		}
	}else	{
		ed_confyn.Text = "N";
	}

	if (ed_confyn.Text == "Y")	{
		grd_list.Editable = false;
	}else	{
		grd_list.Editable = true;
	}

	for(var i=0; i< ds_list.GetRowCount(); i++)
	{
	  if(G_USER_ID == '2018578' or G_USER_ID == '2022702'  or G_USER_ID == '2024586' or G_USER_ID == '2018900'   or G_USER_ID == '2022958' or G_USER_ID == '2019124')
	  {
	   	  ds_list.SetColumn(i, "FLAG2", 'Y' ); 
      } else {ds_list.SetColumn(i, "FLAG2", 'N'); }	
	}


	// org 정보 저장
	ds_list_org.Copy("ds_list");
}

