/********************************************************************************
* 기      능   : 저장
********************************************************************************/
function fn_save()	{

	gfn_showMsg("");

	if (!ds_list.GetUpdate())	{
		gfn_showAlert("CW00005");
		return;
	}

	if (!fn_save_check())	{
		return;
	}

	// 관리자 이외에는 확정여부와 확인 후 처리
	fn_chkConfirm();
}

/********************************************************************************
* 기      능   : 확정(승인) 여부 확인
********************************************************************************/
function fn_chkConfirm()	{
	// 확정(승인) 여부 확인
	// 확정여부 조회
	tit_clearActionInfo();
	tit_addSearchActionInfo("qm07:QM0702002A_S2");

	tit_callService(
          ""
        , ""
        , "ds_cond=ds_cond"
        , "ds_conf=ds_list"
        , ""
        , "fn_afterConfYNQuery2"
        , true);
}

/********************************************************************************
* 기      능   :  미 확정 시 저장 처리
********************************************************************************/
function fn_afterConfYNQuery2(errCode, errMsg) 	{
	var vConfYN = "N";

	var vMsg = "이미 승인(확정)되어 저장 처리를 진행 할 수 없습니다.";

	// 확정 정보를 확인하여 처리
	var vConfDate = ds_conf.GetColumn(0, "CFDATE");
	if (ds_conf.GetRowCount() > 0)	{
		if(vConfDate == null || replace(vConfDate, "00000000", "") == "")	{
			vConfYN = "N";
		}else	{
			vConfYN = "Y";
		}
	}else	{
		vConfYN = "N";
	}

	if (vConfYN == "Y")	{
		gfn_showAlert(vMsg);
		return;
	}

	fn_saveStym();
}

/********************************************************************************
* 기      능   :  저장 처리 (최초실적시작월)
********************************************************************************/
function fn_saveStym() 	{

	if (!gfn_showConfirm("CI00008"))	{
		return;
	}

	// 시작월 등록 처리 dataset setting
	ds_list_stym.ClearData();
	for (var i = 0; i < ds_list.GetRowCount(); i++)		{
		var vStym = ds_list.GetColumn(i, "STYM");
		if (ds_list.GetColumn(i, "FLAG") == "U")	{
			if (vStym == null || replace(vStym, "000000", "") == "")		{
				var vAddRow = 0;
				vAddRow = ds_list_stym.AddRow();
				ds_list_stym.SetColumn(vAddRow, "FLAG",  "U");
				ds_list_stym.SetColumn(vAddRow, "DEPT",  ds_list.GetColumn(i, "DEPT"));
				ds_list_stym.SetColumn(vAddRow, "GJAHR", ds_list.GetColumn(i, "GJAHR"));
				ds_list_stym.SetColumn(vAddRow, "ITEM",  ds_list.GetColumn(i, "ITEM"));
				ds_list_stym.SetColumn(vAddRow, "STYM",  substr(cal_rsltym.Value, 0, 6));
			}
			else if(ds_list.GetColumn(i, "FLAG2") == "Y")
			{
			   // alert("a");
		        var vAddRow = 0;
				vAddRow = ds_list_stym.AddRow();
				ds_list_stym.SetColumn(vAddRow, "FLAG",  "U");
				ds_list_stym.SetColumn(vAddRow, "DEPT",  ds_list.GetColumn(i, "DEPT"));
				ds_list_stym.SetColumn(vAddRow, "GJAHR", ds_list.GetColumn(i, "GJAHR"));
				ds_list_stym.SetColumn(vAddRow, "ITEM",  ds_list.GetColumn(i, "ITEM"));
				ds_list_stym.SetColumn(vAddRow, "STYM",  ds_list.GetColumn(i, "STYM"));
			}
		}
	}

	tit_clearActionInfo();
	tit_addSaveActionInfo(
		  ""
		, "qm07:QM0702002A_U1"
		, ""
		, "FLAG");

	tit_callService(
          ""
        , ""
        , "ds_list=ds_list_stym:U"
        , ""
        , ""
        , "fn_saveProc");
}

/********************************************************************************
* 기      능   :  저장 처리 
********************************************************************************/
function fn_saveProc(errCode, errMsg) 	{

	if ( errCode != 0 ) {
		gfn_showMsg("CE00001");
		return;
	} 

	tit_clearActionInfo();
	tit_addSaveActionInfo(
		  ""
		, "qm07:QM0702002A_I1"
		, ""
		, "FLAG");

	tit_callService(
          ""
        , ""
        , "ds_list=ds_list:U"
        , ""
        , ""
        , "fn_afterSaveProc");
}

/********************************************************************************
* 기      능   : 저장 Callback  
********************************************************************************/
function fn_afterSaveProc(errCode, errMsg) 	{
	if ( errCode != 0 ) {
		gfn_showMsg("CE00001");
	} else {
		gfn_showMsg("CI00003");		
	}

	fn_query();
}

/********************************************************************************
* 기      능   : 저장 전 유효성 체크
********************************************************************************/
function fn_save_check()	{
	for (var nRow = 0; nRow < ds_list.GetRowCount(); nRow++)	{
		if (ds_list.GetColumn(nRow, "FLAG") == "U")
		{
			// 처리기준
			if (!fn_dataSetIsNullCheck(ds_list, nRow, "KGBN"))	{
				var arr = [nRow+1, "처리기준"];
				gfn_showMsg("CW00224",arr);
				fn_SetFocusGrid(grd_list, nRow, gfn_getColIdx(grd_list,'KGBN'));
				return false;
			}

			//절감전
			if (!fn_dataSetIsNullCheck(ds_list, nRow, "BFAMT"))	{
				var arr = [nRow+1, "절감전금액"];
				gfn_showMsg("CW00224",arr);
				fn_SetFocusGrid(grd_list, nRow, gfn_getColIdx(grd_list,'BFAMT'));
				return false;
			}
			if (ds_list.GetColumn(nRow, "BFAMT") < 0)	{
				var arr = [nRow+1, "절감전금액"];
				gfn_showMsg("CW00055",arr);
				fn_SetFocusGrid(grd_list, nRow, gfn_getColIdx(grd_list,'BFAMT'));
				return false;
			}

			//절감후
			if (!fn_dataSetIsNullCheck(ds_list, nRow, "AFAMT"))	{
				var arr = [nRow+1, "절감후금액"];
				gfn_showMsg("CW00224",arr);
				fn_SetFocusGrid(grd_list, nRow, gfn_getColIdx(grd_list,'AFAMT'));
				return false;
			}
			if (ds_list.GetColumn(nRow, "AFAMT") < 0)	{
				var arr = [nRow+1, "절감후금액"];
				gfn_showMsg("CW00055",arr);
				fn_SetFocusGrid(grd_list, nRow, gfn_getColIdx(grd_list,'AFAMT'));
				return false;
			}

			//if (toNumber(ds_list.GetColumn(nRow, "BFAMT")) == 0 && toNumber(ds_list.GetColumn(nRow, "AFAMT")) == 0)	{
			//	var arr = [nRow+1, "절감전 또는 절감후금액"];
			//	gfn_showMsg("CW00055",arr);
			//	fn_SetFocusGrid(grd_list, nRow, gfn_getColIdx(grd_list,'BFAMT'));
			//	return false;
			//}

			// if (toNumber(ds_list.GetColumn(nRow, "BFAMT")) < toNumber(ds_list.GetColumn(nRow, "AFAMT")))	{
				// gfn_showMsg((nRow+1) + "번째 " + "절감후금액은 절감전금액보다 클 수 없습니다.");
				// fn_SetFocusGrid(grd_list, nRow, gfn_getColIdx(grd_list,'AFAMT'));
				// return false;
			// }

			//수량
			if (!fn_dataSetIsNullCheck(ds_list, nRow, "QNTY"))	{
				var arr = [nRow+1, "적용수량"];
				gfn_showMsg("CW00224",arr);
				fn_SetFocusGrid(grd_list, nRow, gfn_getColIdx(grd_list,'QNTY'));
				return false;
			}
			if (ds_list.GetColumn(nRow, "QNTY") < 0)	{
				var arr = [nRow+1, "적용수량"];
				gfn_showMsg("CW00055",arr);
				fn_SetFocusGrid(grd_list, nRow, gfn_getColIdx(grd_list,'QNTY'));
				return false;
			}
			//절감금액
			if (!fn_dataSetIsNullCheck(ds_list, nRow, "SVAMT"))	{
				var arr = [nRow+1, "절감단가"];
				gfn_showMsg("CW00224",arr);
				fn_SetFocusGrid(grd_list, nRow, gfn_getColIdx(grd_list,'SVAMT'));
				return false;
			}
  			if (ds_list.GetColumn(nRow, "FLAG3") == 'N')	{
				var arr = [nRow+1, "Grouping 상태에선 저장을 할 수 없습니다."];
				gfn_showMsg("CW10025",arr);
				fn_SetFocusGrid(grd_list, nRow, gfn_getColIdx(grd_list,'FLAG3'));
				return false;
			}
			// if (ds_list.GetColumn(nRow, "SVAMT") < 0)	{
				// var arr = [nRow+1, "절감단가"];
				// gfn_showMsg("CW00055",arr);
				// fn_SetFocusGrid(grd_list, nRow, gfn_getColIdx(grd_list,'SVAMT'));
				// return false;
			// }
		}
	}
	return true;
}

