/********************************************************************************
* 기      능   : 변경전 정보로 취소 (원 Data로 변경)
********************************************************************************/
function fn_cancel_OnClick()
{
	var vRow = ds_list.currow;

	if (ds_list.GetColumn(vRow, "FLAG") == "U")		{
		var vSearchFilter = "DEPT=="      + quote(ds_list.GetColumn(vRow, "DEPT"))  +
							"&& GJAHR=="  + quote(ds_list.GetColumn(vRow, "GJAHR")) +
							"&& ITEM=="   + quote(ds_list.GetColumn(vRow, "ITEM"))  +
							"&& RSLTYM==" + quote(ds_list.GetColumn(vRow, "RSLTYM"));

		var vSearchRow = ds_list_org.SearchRow(vSearchFilter);
		if (vSearchRow > -1)	{
			ds_list.onColumnChanged = "";

			ds_list.SetColumn(vRow, "FLAG",    ds_list_org.GetColumn(vSearchRow, "FLAG"));
			ds_list.SetColumn(vRow, "KGBN",    ds_list_org.GetColumn(vSearchRow, "KGBN"));
			ds_list.SetColumn(vRow, "BFAMT",   ds_list_org.GetColumn(vSearchRow, "BFAMT"));
			ds_list.SetColumn(vRow, "AFAMT",   ds_list_org.GetColumn(vSearchRow, "AFAMT"));
			ds_list.SetColumn(vRow, "QNTY",    ds_list_org.GetColumn(vSearchRow, "QNTY"));
			ds_list.SetColumn(vRow, "SVAMT",   ds_list_org.GetColumn(vSearchRow, "SVAMT"));
			ds_list.SetColumn(vRow, "RSLTAMT", ds_list_org.GetColumn(vSearchRow, "RSLTAMT"));
			ds_list.SetColumn(vRow, "BIGO",    ds_list_org.GetColumn(vSearchRow, "BIGO"));

			ds_list.onColumnChanged = "fn_ds_list_OnColumnChanged";
		}
	}
}

