/********************************************************************************
* 기      능   : 엑셀 자료가 import가 완료된 시점
********************************************************************************/
function fn_afterExcelImport(dsTarget, dsSource)
{

	dsTarget.FireEvent = false;

	// 정보 확인 반영
	for (var i = 0; i < dsSource.GetRowCount(); i++)	{
		var vChange = "";

		// ITEM이 존재하는 경우만 처리
		if (length(trim(dsSource.GetColumn(i, "ITEM"))) > 0)	{
			var vSerach = "ITEM==" + quote(replace(dsSource.GetColumn(i, "ITEM"), "-", ""));

			var vRow = dsTarget.SearchRow(vSerach);
			if (vRow > -1)	{	// 해당 정보가 존재

				var vKgbn = decode(dsSource.GetColumn(i, "KGBN"), "총액기준", "T", "단가기준", "U", "");
				if (length(trim(dsTarget.GetColumn(vRow, "KGBN"))) != length(trim(vKgbn)))	{
					if(dsTarget.GetColumn(vRow, "KGBN") != vKgbn)	{
						dsTarget.SetColumn(vRow, "KGBN", vKgbn);
						vChange = "C";
					}
				}

				if (toNumber(dsTarget.GetColumn(vRow, "BFAMT")) != toNumber(dsSource.GetColumn(i, "BFAMT")))	{
					dsTarget.SetColumn(vRow, "BFAMT",   dsSource.GetColumn(i, "BFAMT"));
					vChange = "C";
				}

				if (toNumber(dsTarget.GetColumn(vRow, "AFAMT")) != toNumber(dsSource.GetColumn(i, "AFAMT")))	{
					dsTarget.SetColumn(vRow, "AFAMT",   dsSource.GetColumn(i, "AFAMT"));
					vChange = "C";
				}

				if (toNumber(dsTarget.GetColumn(vRow, "QNTY")) != toNumber(dsSource.GetColumn(i, "QNTY")))	{
					dsTarget.SetColumn(vRow, "QNTY",    dsSource.GetColumn(i, "QNTY"));
					vChange = "C";
				}

				if (toNumber(dsTarget.GetColumn(vRow, "SVAMT")) != toNumber(dsSource.GetColumn(i, "SVAMT")))	{
					dsTarget.SetColumn(vRow, "SVAMT",   dsSource.GetColumn(i, "SVAMT"));
					vChange = "C";
				}

				if (toNumber(dsTarget.GetColumn(vRow, "RSLTAMT")) != toNumber(dsSource.GetColumn(i, "RSLTAMT")))	{
					dsTarget.SetColumn(vRow, "RSLTAMT", dsSource.GetColumn(i, "RSLTAMT"));
					vChange = "C";
				}

				// if (toNumber(dsSource.GetColumn(i, "SVAMT")) == 0)	{
					// dsTarget.SetColumn(vRow, "SVAMT",   toNumber(dsSource.GetColumn(i, "BFAMT")) - toNumber(dsSource.GetColumn(i, "AFAMT")));
				// }else	{
					// dsTarget.SetColumn(vRow, "SVAMT",   dsSource.GetColumn(i, "SVAMT"));
				// }

				if (length(trim(dsTarget.GetColumn(vRow, "BIGO"))) != length(trim(dsSource.GetColumn(i, "BIGO"))))	{
					if (trim(dsTarget.GetColumn(vRow, "BIGO")) != trim(dsSource.GetColumn(i, "BIGO")))	{
						dsTarget.SetColumn(vRow, "BIGO",    dsSource.GetColumn(i, "BIGO"));
						vChange = "C";
					}
				}

				if (vChange != "")	{
					// 정보가 존재하지 않는 경우 "수기"
					if(length(trim(dsTarget.Getcolumn(vRow, "SGBN"))) == 0)		{
						dsTarget.SetColumn(vRow, "SGBN", "M");
					}

					dsTarget.SetColumn(vRow, "FLAG", "U");
				}
			}
		}
	}

	dsTarget.FireEvent = true;
//TRACE(dsSource.savexml());
//ALERT("aaa");			
//TRACE(dsTarget.savexml());
//ALERT("bbb");		
}

/*********************************************************************
* Function : 엑셀파일을 DataGrid로 업로드
* Argument : objGrid	- 엑셀업로드 대상이 되는 그리드
*            strDsName	- 그리드에 매핑된 데이타셋 이름
* Return   : 
**********************************************************************/
function ufn_ExcelToGrid(objGrid, strDsName){
	//Dialog 생성
	if(!IsValid(object("_fdgCommon"))){
		Create( "FileDialog" , "_fdgCommon");
	}
	_fdgCommon.Type = "Open";
	_fdgCommon.Filter = "Excel 통합문서 (*.xlsx)|*.xlsx|Excel 97-2003 통합문서 (*.xls)|*.xls|";

	//실패시 컴포넌트 제거
	if(!_fdgCommon.Open()){
		Destroy("_fdgCommon");
	}

	objGrid.redraw = false;
	var IRtn = ext_ExcelImportByIndex(_fdgCommon.FilePath +"\\"+_fdgCommon.FileName,0,strDsName,1, 1, 0, 2,,);
	objGrid.redraw = true;
	Destroy("_fdgCommon");
}

/********************************************************************************
* 기      능   : 최초실적 적용년월 초기화
********************************************************************************/
function fn_init_OnClick(obj)
{
	var vRow = ds_list.currow;

    if( ds_list.GetRowCount() > 0 )
    {
		if( ds_list.GetColumn(vRow, "STYM") == substr(cal_rsltym.Value, 0, 6) && ds_list.GetColumn(vRow, "SGBN") == 'M')
		{

			if ( ed_confyn.Text == 'Y' )
			{
				var vMsg = "이미 승인(확정)되어 초기화를 진행 할 수 없습니다.";
				gfn_showAlert("CW10025", vMsg);
				return;
			}


			ds_init.ClearData();
			ds_init.InsertRow(0);
		    ds_init.SetColumn(0, "DEPT",   	ds_list.GetColumn(vRow, "DEPT"));
		    ds_init.SetColumn(0, "GJAHR",  	ds_list.GetColumn(vRow, "GJAHR"));
		    ds_init.SetColumn(0, "ITEM",   	ds_list.GetColumn(vRow, "ITEM"));
		    ds_init.SetColumn(0, "RSLTYM",  ds_list.GetColumn(vRow, "RSLTYM"));

	        //============타월에 자료가 있는지 체크(ZQMT076)==============
			tit_clearActionInfo();
			tit_addSearchActionInfo("qm07:QM0702002A_S3");
			tit_callService(
				  ""
				, ""
				, "ds_init=ds_init"
				, "ds_init_ck=ds_init_ck"
				, ""
				, "" //"fn_afterQuery"
				, ""
				, ""
				, true);

			if( ds_init_ck.GetColumn(0, "CNT") > 0 )
			{
				var vMsg = "선택하신 항목의 실적이 2건이상 존재합니다. .";
				gfn_showAlert("CW10025", vMsg);
				return;
			}	    
	        //=====================================================


			tit_clearActionInfo();			   
			tit_addSingleActionInfo("qm07:QM0702002A_U2");     
			tit_addSingleActionInfo("qm07:QM0702002A_D1");     
			tit_callService(
				""
				, ""
				, "ds_init=ds_init"
				, ""
				, ""
				, "" //"fn_afterclosed"
				, ""
				, ""
				, true);

			fn_query();
		}		
		else
		{
			var vMsg = "최초실적 적용년월이 미존재/수기/실적년월과 년월 불일치로 처리할수 없습니다.";
			gfn_showAlert("CW10025", vMsg);
			return;
		}
    }



}



