/********************************************************************************
* 기      능   :  절감금액 계산
********************************************************************************/
function ds_list_OnColumnChanged(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex)
{
   var arr = ds_list.GetColumn(ds_list.currow,"QNTY_CH") * ds_list.GetColumn(ds_list.currow,"NETWR_CH") ; //절감금액(변경 전)
   var arr2 = ds_list.GetColumn(ds_list.currow,"QNTY") * ds_list.GetColumn(ds_list.currow,"NETWR") ; //절감금액(변경 후)

   ds_list.SetColumn(ds_list.currow,"SVAMT2",arr);
   ds_list.SetColumn(ds_list.currow,"SVAMT3",arr2);
   ds_list.SetColumn(ds_list.currow,"SVAMT",arr - arr2); // 절감전 - 절감 후 (최종 절감 금액)

}

function grd_list_OnKillFocus(obj)
{
	fn_ds_list_OnColumnChanged();
}

