********************************************************************************/
/********************************************************************************
* 기      능   : 삭제  -- 작업 완료일 리셋 
********************************************************************************/
function fn_delete() 
{

  if(P_CONFYN == 'Y')  //확정시 삭제 불가 
   { var arr = "확정된 데이터 입니다." ;
  	gfn_showMsg('CW10025' ,arr);
    return;
   }

  if(ds_list.FindRow("CHECK", 1)==-1){
	 gfn_showMsg('CW00046');
	 return;
   }

  if(!gfn_showConfirm( "CI00015", "해당 데이터" )) return;	

  	ds_list.Filter("CHECK==1");
	ds_list2.copyF(ds_list);
	ds_list.unFilter();

	tit_clearActionInfo();			
	tit_addMultiActionInfo("qm07:QM0702004B_D1");
	tit_callService(
		""
		, ""
		, "ds_list=ds_list2"
		, ""
		, ""
		, "fn_afterDelete"
		, true);	
}

/********************************************************************************
* 기      능   : 삭제 Callback  
********************************************************************************/
function fn_afterDelete(errCode, errMsg) {
	if ( errCode!= 0 ) {
		gfn_showMsg("CE00001");
	} else {
		gfn_showMsg("CI00003");
	}	
   fn_query();
}
