/********************************************************************************
* 기      능   : 엑셀 자료가 import가 완료된 시점
********************************************************************************/
function fn_afterExcelImport(dsTarget, dsSource)
{
     dsTarget.ClearData();

	// 정보 확인 반영
	for (var i = 0; i < dsSource.GetRowCount(); i++)	{
		var vRow = dsTarget.AddRow();

			//	if (toNumber(dsTarget.GetColumn(vRow, "WBS")) != toNumber(dsSource.GetColumn(i, "WBS")))	{
					dsTarget.SetColumn(vRow, "WBS",   dsSource.GetColumn(i, "WBS"));
				//}
                //변경전 //

					dsTarget.SetColumn(vRow, "MATNRNM_CH",   dsSource.GetColumn(i, "MATNRNM_CH")); //품명
					dsTarget.SetColumn(vRow, "MATKL_CH",   dsSource.GetColumn(i, "MATKL_CH"));//BLOCK NO

					dsTarget.SetColumn(vRow, "MATNR_CH",    dsSource.GetColumn(i, "MATNR_CH")); //자재번호
					dsTarget.SetColumn(vRow, "BFMATNR_CH",    dsSource.GetColumn(i, "BFMATNR_CH")); //도면번호
					dsTarget.SetColumn(vRow, "NETWR_CH",    dsSource.GetColumn(i, "NETWR_CH")); //단가

				//if (toNumber(dsTarget.GetColumn(vRow, "QNTY_CH")) != toNumber(dsSource.GetColumn(i, "QNTY_CH")))	{
					dsTarget.SetColumn(vRow, "QNTY_CH",   dsSource.GetColumn(i, "QNTY_CH")); //수량
				//}

					dsTarget.SetColumn(vRow, "UNIT_CH", dsSource.GetColumn(i, "UNIT_CH")); // 단위 

				//if (toNumber(dsTarget.GetColumn(vRow, "SVAMT2")) != toNumber(dsSource.GetColumn(i, "SVAMT2")))	{
					dsTarget.SetColumn(vRow, "SVAMT2",   dsSource.GetColumn(i, "SVAMT2")); //금액 (변경전)
				//}

		    //변경후//

					dsTarget.SetColumn(vRow, "MATNRNM",   dsSource.GetColumn(i, "MATNRNM")); //품명
					dsTarget.SetColumn(vRow, "MATKL",   dsSource.GetColumn(i, "MATKL"));//BLOCK NO

					dsTarget.SetColumn(vRow, "MATNR",    dsSource.GetColumn(i, "MATNR")); //자재번호
					dsTarget.SetColumn(vRow, "BFMATNR",    dsSource.GetColumn(i, "BFMATNR")); //도면번호
					dsTarget.SetColumn(vRow, "NETWR",    dsSource.GetColumn(i, "NETWR")); //단가


					dsTarget.SetColumn(vRow, "QNTY",   dsSource.GetColumn(i, "QNTY")); //수량


					dsTarget.SetColumn(vRow, "UNIT", dsSource.GetColumn(i, "UNIT")); // 단위 

				//if (toNumber(dsTarget.GetColumn(vRow, "SVAMT3")) != toNumber(dsSource.GetColumn(i, "SVAMT3")))	{
					dsTarget.SetColumn(vRow, "SVAMT3",   dsSource.GetColumn(i, "SVAMT3")); //금액 (변경후 )
				//}

				//if (toNumber(dsTarget.GetColumn(vRow, "SVAMT")) != toNumber(dsSource.GetColumn(i, "SVAMT")))	{
					dsTarget.SetColumn(vRow, "SVAMT", dsSource.GetColumn(i, "SVAMT")); //(절감금액)
				//}

				dsTarget.SetColumn(vRow, "DEPT", cb_dept.Value); //부서
				dsTarget.SetColumn(vRow, "GJAHR", substr(cal_rsltym.Value,0,4)); //실적년도
				dsTarget.SetColumn(vRow, "ITEM", cb_item.Value);
				dsTarget.SetColumn(vRow, "RSLTYM", substr(cal_rsltym.Value,0,6)); //실적제출월
				dsTarget.SetColumn(vRow, "WAERK", 'KRW');

		}


	//dsTarget.FireEvent = true;
//TRACE(dsSource.savexml());
//ALERT("aaa");			
//TRACE(dsTarget.savexml());
//ALERT("bbb");		
}

/*********************************************************************
* Function : 엑셀파일을 DataGrid로 업로드
* Argument : objGrid	- 엑셀업로드 대상이 되는 그리드
*            strDsName	- 그리드에 매핑된 데이타셋 이름
* Return   : 
**********************************************************************/
function ufn_ExcelToGrid(objGrid, strDsName){
	//Dialog 생성
	if(!IsValid(object("_fdgCommon"))){
		Create( "FileDialog" , "_fdgCommon");
	}
	_fdgCommon.Type = "Open";
	_fdgCommon.Filter = "Excel 통합문서 (*.xlsx)|*.xlsx|Excel 97-2003 통합문서 (*.xls)|*.xls|";

	//실패시 컴포넌트 제거
	if(!_fdgCommon.Open()){
		Destroy("_fdgCommon");
	}

	objGrid.redraw = false;
	var IRtn = ext_ExcelImportByIndex(_fdgCommon.FilePath +"\\"+_fdgCommon.FileName,0,strDsName,1, 1, 0, 1,,);
	objGrid.redraw = true;
	Destroy("_fdgCommon");
}

