// [ PART 1 ]
// Form에서 사용될 전역변수를 선언
// FORM에서 사용되는 전역변수는 대문자로 작성하며, F_XXX 의 형태를 갖도록 한다. 
//=========================================================================================
var F_ADMIN_DEPT = "F2,F6";	// 효율개선부 -> 총무부로 변경(조직개편2016.04), 정보기술실(관리부서)		
var F_ADMIN_YN   = "Y";		// 관리부서 여부
var F_CALLYN     = "N";		// 원가절감실적집계 화면에서 호출여부
var P_CONFYN     = "";
var fv_Rsltym    = "";
//=========================================================================================
// [ PART 2 ]
// Form Loading / Unloading 시 작업 정의
//=========================================================================================
function fn_OnLoadCompleted(obj) {

	// Form 로드 이벤트 맨 첫줄에 기술 
	gfn_initForm(true);

	//일자 세팅
	var sYear = substr(gfn_getCurrDate(),0,4);
	var sMnth = substr(gfn_getCurrDate(),4,2);

	cb_year.Value = sYear;
	cb_month.Value = sMnth;



	// 원가절감 부서정보 조회
	var codeArr    = ["QM01"];
	var dsObjArr   = [ds_dept_cd];
	var addTypeArr = [G_CODE_NONE];
	var oderTypeArr = ["CTEXT2"];
	gfn_searchCommCode2(codeArr, dsObjArr, addTypeArr, oderTypeArr);




}

/********************************************************************************
* 기      능   : form UnloadCompleted 
********************************************************************************/
function fn_OnUnloadCompleted(obj) {
	return gfn_isFormUnload(obj);
}

/********************************************************************************
* 기      능   : 공통코드가 조회된 후의 Callback 함수 
********************************************************************************/
function fn_completeInit(sCode, sDsObj, sCodeType) {

	cb_dept.Value = G_DPT1;
	// 관리부서가 아닌경우 부서정보 변경 불가
	if (IndexOf(F_ADMIN_DEPT, G_DPT1) == -1)		{
		F_ADMIN_YN = "N";
	}

	// 추후 정보 확인 후 삭제 필요 &&&&
	if (length(trim(G_DPT1)) == 0)	{
		F_ADMIN_YN = "Y";
	}

	if (F_ADMIN_YN == "Y")	{
		cb_dept.Enable = true;
		cb_dept.Index = 0;
	}else	{
		cb_dept.Enable = false;
	}


}

/********************************************************************************
* 기      능   : 조회 (header 조회)
********************************************************************************/
function fn_query() {

	ds_cond.SetColumn(0, "RSLTYM", "");
	fv_Rsltym = "";
	/*
	// 연도는 전체이지만, 월은 존재할 경우
	if(Length(cb_year.Value) == 0 && Length(cb_month.Value) != 0)
	{
		gfn_openMsgWin("실적월을 선택하신 경우, 실적연도를 선택해 주시기 바랍니다.");
	}

	if(Length(cb_year.Value) != 0 && Length(cb_month.Value) != 0)
	{
		ds_cond.SetColumn(0, "RSLTYM", cb_year.Value+cb_month.Value );
	}
	*/


	//실적년월 필드 필수체크	
	if(gfn_isNull(cb_year, true, st_year.value))
		return;

	if(gfn_isNull(cb_month, true, st_month.value))
		return;

	if(gfn_isNull(cb_dept, true, st_dept.value))
		return;

	if(Length(ed_wbs.Text) > 0 && Length(ed_wbs.Text) < 9)
	{
		gfn_openMsgWin("WBS를 입력해 주십시오(9자리 필수)");
		return;
	}


	if(Length(cb_year.Value) != 0 && Length(cb_month.Value) != 0)
	{
		ds_cond.SetColumn(0, "RSLTYM", cb_year.Value+cb_month.Value );
	}

	var sRsltym = ds_cond.GetColumn(0, "RSLTYM");
	fv_Rsltym = sRsltym;

	// 실적처리 일자 조회 (현장 정보 성능 향상을 위한 처리)
	tit_clearActionInfo();
	tit_addSearchActionInfo("qm07:QM0702003B_S2");
	tit_callService(
          ""
        , ""
        , "ds_cond=ds_cond"
        , "ds_crdat=ds_list"
        , ""
        , "fn_afterCrdatQuery"
        , true);




	//http.Sync = false;


}

/********************************************************************************
* 기      능   : 실적처리 일자 조회 (현장 정보 성능 향상을 위한 처리) call back
********************************************************************************/
function fn_afterCrdatQuery(errCode, errMsg) {
	// 실적처리일자 정보
	if (ds_crdat.GetRowCount() > 0)	{
		if(fv_Rsltym == '')
		{
			ds_cond.SetColumn(0, "S_CRDAT", '20000101');
			ds_cond.SetColumn(0, "E_CRDAT", '20991230');
		}
		else
		{
			ds_cond.SetColumn(0, "S_CRDAT", ds_crdat.GetColumn(0, "S_CRDAT"));
			ds_cond.SetColumn(0, "E_CRDAT", ds_crdat.GetColumn(0, "E_CRDAT"));
		}

	}else	{
		if(fv_Rsltym != '')
		{
			var vScrdat = left(fv_Rsltym, 6) + "01";
			var vEcrdat = left(fv_Rsltym, 6) + gfn_getLastDay(toNumber(right(left(sRsltym, 6), 2)));
			ds_cond.SetColumn(0, "S_CRDAT", vScrdat);
			ds_cond.SetColumn(0, "E_CRDAT", vEcrdat);
		}
		else
		{
			ds_cond.SetColumn(0, "S_CRDAT", '20000101');
			ds_cond.SetColumn(0, "E_CRDAT", '20991230');
		}

	}

	// 항목의 상세조건 조회
	tit_clearActionInfo();
	tit_addSearchActionInfo("qm07:QM0702005A_S1");
	tit_callService(
          ""
        , ""
        , "ds_cond=ds_cond"
        , "ds_detail_cond=ds_list"
        , ""
        , "fn_afterDetailCondQuery"
        , true);
}

