#include "LIB::commons.js";	// 공통 스크립트 
//=========================================================================================
// [ PART 1 ]
// Form에서 사용될 전역변수를 선언
// FORM에서 사용되는 전역변수는 대문자로 작성하며, F_XXX 의 형태를 갖도록 한다. 
//=========================================================================================

var F_ADMIN_DEPT = "F2,F6";	// 효율개선부 -> 총무부로 변경(조직개편2016.04), 정보기술실(관리부서)		
var F_ADMIN_YN   = "Y";		// 관리부서 여부
var F_QUERY_YN   = "N";		// 조회여부
var F_CONFDT     = "00000000";		// 확정일자 
var F_DEPT       = "D5";		// 구매부  인상은 월별금액이 -가 포함됨

//=========================================================================================
// [ PART 2 ]
// Form Loading / Unloading 시 작업 정의
//=========================================================================================
function fn_OnLoadCompleted(obj) {
	// Form 로드 이벤트 맨 첫줄에 기술 
	gfn_initForm(true);

	var curYear = substr(gfn_getCurrDate(),0,4);
	fn_makeYear(curYear,-2,1);

	cb_gjahr.SetFocus();

	// 승인 버튼명을 확정으로 변경
	btn_approval.DomainID = "";
	btn_approval.Text     = "확정";

	// 관리부서가 아닌경우 부서정보 변경 불가
	if (IndexOf(F_ADMIN_DEPT, G_DPT1) == -1)		{
		F_ADMIN_YN = "N";
	}

	// 추후 정보 확인 후 삭제 필요 &&&&
	if (length(trim(G_DPT1)) == 0)	{
		F_ADMIN_YN = "Y";
	}

	// 부서정보  활성화
	if (F_ADMIN_YN == "Y")		{
		cb_dept.Enable      = true;
		cb_dept.Value       = "";
	}else	{
		cb_dept.Enable      = true;
	}

	if(F_ADMIN_YN == "Y")
	{
		// 원가절감 부서정보 조회
		var codeArr    = ["QM01"];
		var dsObjArr   = [ds_dept_cd];
		var addTypeArr = [G_CODE_NONE];
		gfn_searchCommCode(codeArr, dsObjArr, addTypeArr);
	}
	else
	{
		// 로그인 사용자별 권한에 따른 부서정보 조회
		tit_clearActionInfo();
		tit_addSearchActionInfo("qm07:QM0701001A_S4");

		tit_callService(
			  ""
			, ""
			, "ds_cond=ds_cond"
			, "ds_dept_cd=ds_dept_cd"
			, ""
			, "fn_completeInit"
			, true);
	}

}

/********************************************************************************
* 기      능   : form UnloadCompleted 
********************************************************************************/
function fn_OnUnloadCompleted(obj) {
	return gfn_isFormUnload(obj);
}

/********************************************************************************
* 기      능   : 기준년도 계산(현재년도,당해년도에서 뺄 년수, 당해년도에서 더할 년수
********************************************************************************/
function fn_makeYear(curYear,sNum,eNum)
{
	var rowCnt = eNum-sNum;
	var row = 0;

	for(var i=rowCnt; i>-1; i--)
	{
		ds_gjahr_cd.AddRow();
		ds_gjahr_cd.SetColumn(row,"CODE",parseInt(curYear)+i+parseInt(sNum));
		ds_gjahr_cd.SetColumn(row,"CODE_NAME",(parseInt(curYear)+i+parseInt(sNum))+"년");
		row++;
	}
	cb_gjahr.index = eNum;
}

/********************************************************************************
* 기      능   : 조회 (header 조회)
********************************************************************************/
function fn_query() {
	btn_part.Enable    = false;
	btn_pdmcase.Enable = false;

	F_CONFDT = "00000000";

	gfn_showMsg("");

	//년도필드 필수체크	
	if(gfn_isNull(cb_gjahr, true, st_gjahr.value))
		return;

	if(gfn_isNull(cb_dept, true, st_dept.value))
		return;

	// HTTP SESSION TIMEOUT을 변경
	//http.TimeOut = 1800;

	tit_clearActionInfo();
	tit_addSearchActionInfo("qm07:QM0706001A_S1");

	tit_callService(
          ""
        , ""
        , "ds_cond=ds_cond"
        , "ds_list=ds_list"
        , ""
        , "fn_afterQuery"
        , true);
}
/********************************************************************************
* 기      능   : 조회 Callback  
********************************************************************************/
function fn_afterQuery(errCode, errMsg) {

	if (errCode = 0)	{
		F_QUERY_YN = "Y";	// 정상조회
	}

	gfn_showSearchMsg(grd_list, errCode, errMsg);
	//gfn_showMsg("CI00002",ds_list.RowCount()+"");

	if (ds_list.GetRowCount() > 0)	{
		// 입력,삭제, 저장 버튼 및 grid 제어
		// 해당 부서에 확정건이 존재
		if (length(trim(ds_list.GetColumn(0, "CONFDT"))) > 0 &&
			ds_list.GetColumn(0, "CONFDT") != "00000000")	{
			if (F_ADMIN_YN != "Y")		{
				btn_delete.Enable = false;
			}else	{
				btn_delete.Enable = true;
			}
			F_CONFDT = ds_list.GetColumn(0, "CONFDT");
		}else	{
			btn_delete.Enable = true;
		}

		fn_grd_list_OnCellClick(grd_list, ds_list.currow);
	}
}

/********************************************************************************
* 기      능   : 공통코드가 조회된 후의 Callback 함수 
********************************************************************************/
function fn_completeInit(sCode, sDsObj, sCodeType) {
    ds_dept_cd.Filter("CODE<>'CK'");
	cb_dept.Value = G_DPT1;


	// 부서정보 및 확정버튼 활성화
	if (F_ADMIN_YN == "Y")		{
		btn_approval.Enable = true;

	}else	{
		btn_approval.Enable = false;

	}
}

