// [ PART 3 ]
// Main Process Event 정의(조회,입력,삭제,저장,인쇄,form닫기)
//=========================================================================================

/********************************************************************************
* 기      능   : 입력  
********************************************************************************/
function fn_input()	{

	gfn_showMsg("");

	//년도필드 필수체크	
	if(gfn_isNull(cb_gjahr, true, st_gjahr.value))
		return;

	if(gfn_isNull(cb_dept, true, st_dept.value))
		return;

	if (F_QUERY_YN == "N")	{
		gfn_showMsg("CW00054");
		return;
	}

	//년도필드 필수체크	
	if(gfn_isNull(cb_gjahr, true, st_gjahr.value))
		return;

	if(gfn_isNull(cb_dept, true, st_dept.value))
		return;

	btn_part.Enable    = false;
	btn_pdmcase.Enable = false;

	// 레코드 추가
	ds_list.AddRow();	
}

/********************************************************************************
* 기      능   : 삭제 
********************************************************************************/
function fn_delete()	{

	gfn_showMsg("");

	var vMsg = "삭제 대상을";
	if (ds_list.currow < 0)	{
		gfn_showMsg("CW00116", vMsg);
		return;
	}

	var vFlag = ds_list.GetColumn(ds_list.currow, "FLAG");

	if (vFlag == "I")	{
		// 레코드 삭제 (추가건으로 화면상에서 제거)
		ds_list.DeleteRow(ds_list.currow);
		return;
	}else	{
		if (F_ADMIN_YN == "Y")		{	// 관리자면 무조건 삭제
			if (vFlag == "D")	{
				// 레코드 상태 원복
				ds_list.SetColumn(ds_list.currow, "FLAG",  "");
			}else	{
				// 레코드 삭제 상태로 변경
				ds_list.SetColumn(ds_list.currow, "FLAG",  "D");
			}
		}else	{
			var vConfDt = ds_list.GetColumn(ds_list.currow, "CONFDT");
			if (length(trim(vConfDt)) == 0 || vConfDt == "00000000")	{
				if (vFlag == "D")	{
					ds_list.SetColumn(ds_list.currow, "FLAG",  "");
				}else	{
					ds_list.SetColumn(ds_list.currow, "FLAG",  "D");
				}
			}else	{
				var vMsg = "확정되어 삭제가";
				gfn_showMsg("CW00114", vMsg);
				return;
			}
		}
	}
}

/********************************************************************************
* 기      능   : 저장
********************************************************************************/
function fn_save()	{

	gfn_showMsg("");

	if (!ds_list.GetUpdate())	{
		gfn_showAlert("CW00005");
		return;
	}

	if (!fn_save_check())	{
		return;
	}

	// 확정일자 확인
	fn_chkConfirm();
}

/********************************************************************************
* 기      능   : 확정 여부 확인
********************************************************************************/
function fn_chkConfirm()	{
	// 확정(승인) 여부 확인
	tit_clearActionInfo();
	tit_addSearchActionInfo("qm07:QM0706001A_S2");
	tit_callService(
		  ""
		, ""
		, "ds_cond=ds_cond"
		, "ds_cnt=ds_cnt"
		, ""
		, "fn_afterChkConfirm"
		, true);
}

/********************************************************************************
* 기      능   :  미 확정 시 저장 처리
********************************************************************************/
function fn_afterChkConfirm(errCode, errMsg) {

	if(ds_cnt.GetRowCount() > 0 && ds_cnt.GetColumn(0, "CNT") > 0) {
		// 확정일자 재 확인
		F_CONFDT = iif(length(trim(ds_cnt.GetColumn(0, "CONFDT"))) == 0, "00000000", ds_cnt.GetColumn(0, "CONFDT"));
	}else	{
		F_CONFDT = "00000000";
	}

	fn_saveProc();
}

/********************************************************************************
* 기      능   :  저장 처리
********************************************************************************/
function fn_saveProc() 	{

	if (!gfn_showConfirm("CI00008"))	{
		return;
	}

	var vParm = "pConfDt=" + quote(F_CONFDT);

	tit_callService(
          "QM0701001A_ACT"
        , "save2"                 //절감 1, 상승 2
        , "ds_list=ds_list:A"
        , ""
        , vParm
        , "fn_afterSaveProc"
        , true);
}

/********************************************************************************
* 기      능   : 저장 Callback  
********************************************************************************/
function fn_afterSaveProc(errCode, errMsg) 	{
	if ( errCode != 0 ) {
		gfn_showMsg("CE00001");
	} else {
		gfn_showMsg("CI00003");		
	}
	fn_query();
}

/********************************************************************************
* 기      능   : 승인(확정) 
********************************************************************************/
function fn_approval()	{

	//년도필드 필수체크	
	if(gfn_isNull(cb_gjahr, true, st_gjahr.value))
		return;

	if(gfn_isNull(cb_dept, true, st_dept.value))
		return;

	if (F_QUERY_YN == "N")	{
		gfn_showMsg("CW00054");	// 조회 후 작업이 가능합니다.
		return;
	}

	var vMsg = "확정부서가 아니여서 확정 작업이";
	if (F_ADMIN_YN != "Y")	{
		gfn_showMsg("CW00114", vMsg);
		return;
	}

	vMsg = "확정 대상의";
	if (ds_list.GetRowCount() <= 0)	{
		gfn_showMsg("CW00113", vMsg);
		return;
	}

	if (ds_list.GetUpdate())	{
		var vMsgArr = ["항목", "확정"];
		gfn_showMsg("CW10003", vMsgArr);
		return;
	}

	vMsg = "확정";
	if (!gfn_showConfirm("CI00024", vMsg))	{
		return;
	}

	// 확정(승인) 여부 확인
	tit_clearActionInfo();
	tit_addSearchActionInfo("qm07:QM0706001A_S2");
	tit_callService(
		  ""
		, ""
		, "ds_cond=ds_cond"
		, "ds_cnt=ds_cnt"
		, ""
		, "fn_afterChkConfirm2"
		, true);

}

/********************************************************************************
* 기      능   : 승인(확정) 
********************************************************************************/
function fn_afterChkConfirm2(errCode, errMsg)	{
	var vMsg = "이미 확정되어 있습니다.";
	if(ds_cnt.GetRowCount() > 0 && ds_cnt.GetColumn(0, "CNT") > 0) {
		gfn_showAlert(vMsg);
		return;
	}

	// 부서의 승인(확정)처리
	tit_clearActionInfo();
	tit_addSingleActionInfo("qm07:QM0706001A_U2");
	tit_callService(
	  ""
	, ""
	, "ds_cond=ds_cond:A"
	, ""
	, ""
	, "fn_afterApproval"
	, true);
}

/********************************************************************************
* 기      능   : 승인(확정)  Callback
********************************************************************************/
function fn_afterApproval(errCode, errMsg) 	{
	if(errCode != 0) {
		gfn_showMsg("CE00001");
	} else {
		gfn_showMsg("CI00003");
	}

	fn_query();
}

/********************************************************************************
* 기      능   : 저장 전 유효성 체크
********************************************************************************/
function fn_save_check()	{

	for (var nRow = 0; nRow < ds_list.GetRowCount(); nRow++)	{
		if (ds_list.GetColumn(nRow, "FLAG") == "I" ||
		    ds_list.GetColumn(nRow, "FLAG") == "U")
		{

			// 팀명
			if (!fn_dataSetIsNullCheck(ds_list, nRow, "TEAMNM"))	{
				var arr = [nRow+1, "팀명"];
				gfn_showMsg("CW00224",arr);
				fn_SetFocusGrid(grd_list, nRow, gfn_getColIdx(grd_list,'TEAMNM'));
				return false;
			}		

			// 담당자
			if (!fn_dataSetIsNullCheck(ds_list, nRow, "MAN"))	{
				var arr = [nRow+1, "담당자"];
				gfn_showMsg("CW00224",arr);
				fn_SetFocusGrid(grd_list, nRow, gfn_getColIdx(grd_list,'MAN'));
				return false;
			}			


			// 내역
			if (!fn_dataSetIsNullCheck(ds_list, nRow, "ITEMNM"))	{
				var arr = [nRow+1, "항목"];
				gfn_showMsg("CW00224",arr);
				fn_SetFocusGrid(grd_list, nRow, gfn_getColIdx(grd_list,'ITEMNM'));
				return false;
			}
			//상승전
			if (!fn_dataSetIsNullCheck(ds_list, nRow, "BFAMT"))	{
				var arr = [nRow+1, "상승전금액"];
				gfn_showMsg("CW00224",arr);
				fn_SetFocusGrid(grd_list, nRow, gfn_getColIdx(grd_list,'BFAMT'));
				return false;
			}
			if (ds_list.GetColumn(nRow, "BFAMT") < 0)	{
				var arr = [nRow+1, "상승전금액"];
				gfn_showMsg("CW00055",arr);
				fn_SetFocusGrid(grd_list, nRow, gfn_getColIdx(grd_list,'BFAMT'));
				return false;
			}

			//상승후
			if (!fn_dataSetIsNullCheck(ds_list, nRow, "AFAMT"))	{
				var arr = [nRow+1, "상승후금액"];
				gfn_showMsg("CW00224",arr);
				fn_SetFocusGrid(grd_list, nRow, gfn_getColIdx(grd_list,'AFAMT'));
				return false;
			}
			if (ds_list.GetColumn(nRow, "AFAMT") < 0)	{
				var arr = [nRow+1, "상승후금액"];
				gfn_showMsg("CW00055",arr);
				fn_SetFocusGrid(grd_list, nRow, gfn_getColIdx(grd_list,'AFAMT'));
				return false;
			}
			//if (toNumber(ds_list.GetColumn(nRow, "BFAMT")) < toNumber(ds_list.GetColumn(nRow, "AFAMT")))	{
			if (toNumber(ds_list.GetColumn(nRow, "BFAMT")) > toNumber(ds_list.GetColumn(nRow, "AFAMT")))	{
				//gfn_showMsg((nRow+1) + "번째 " + "상승후금액은 상승전금액보다 클 수 없습니다.");
				gfn_showMsg((nRow+1) + "번째 " + "상승후금액은 상승전금액보다 작은 수 없습니다.");
				fn_SetFocusGrid(grd_list, nRow, gfn_getColIdx(grd_list,'AFAMT'));
				return false;
			}

			//수량
			if (!fn_dataSetIsNullCheck(ds_list, nRow, "QNTY"))	{
				var arr = [nRow+1, "년간적용수량"];
				gfn_showMsg("CW00224",arr);
				fn_SetFocusGrid(grd_list, nRow, gfn_getColIdx(grd_list,'QNTY'));
				return false;
			}
			if (ds_list.GetColumn(nRow, "QNTY") < 0)	{
				var arr = [nRow+1, "년간적용수량"];
				gfn_showMsg("CW00055",arr);
				fn_SetFocusGrid(grd_list, nRow, gfn_getColIdx(grd_list,'QNTY'));
				return false;
			}
			//단위
			if (!fn_dataSetIsNullCheck(ds_list, nRow, "UNIT"))	{
				var arr = [nRow+1, "단위"];
				gfn_showMsg("CW00224",arr);
				fn_SetFocusGrid(grd_list, nRow, gfn_getColIdx(grd_list,'UNIT'));
				return false;
			}
			//적용년월
			if (!fn_dataSetIsNullCheck(ds_list, nRow, "PLANYM"))	{
				var arr = [nRow+1, "적용년월"];
				gfn_showMsg("CW00224",arr);
				fn_SetFocusGrid(grd_list, nRow, gfn_getColIdx(grd_list,'PLANYM'));
				return false;
			}
			if(!fn_planYMCheck(ds_list, nRow, "PLANYM"))	{
				return false;
			}
			//구분
			if (!fn_dataSetIsNullCheck(ds_list, nRow, "GUBUN"))	{
				var arr = [nRow+1, "구분"];
				gfn_showMsg("CW00224",arr);
				fn_SetFocusGrid(grd_list, nRow, gfn_getColIdx(grd_list,"GUBUN"));
				return false;
			}

			// 확정 이전에는 금액 입력 필요
			if (length(trim(F_CONFDT)) == 0 || F_CONFDT == "00000000")	{
				// 월별계획금액 확인
				if(!fn_planAmtCheck(ds_list, nRow))	{
					return false;
				}
			}else	{	// 확정 이후에는 신규 등록건은 월별 계획금액 입력 불가
				if (ds_list.GetColumn(nRow, "FLAG") == "I")	{
					if(!fn_planAmtNonCheck(ds_list, nRow))	{
						return false;
					}
				}
				// 수정 시에는 체크 불가
				//else if (ds_list.GetColumn(nRow, "FLAG") == "U")	{
					// 월별계획금액 확인
				//	if(!fn_planAmtCheck(ds_list, nRow))	{
				//		return false;
				//	}
				//}
			}
		}
	}
	return true;
}

