	close();
}

/********************************************************************************
* 기      능   : 그리드 헤더 클릭시 정렬 처리 
********************************************************************************/
function fn_grd_list_OnHeadClick(obj,nCell,nX,nY,nPivotIndex)
{
	if ( nCell == 0 ) {
		// 현재 처리할 format 기억
		gfn_selectGridCol("grd_list");
	} else {
		gfn_sortGrid(obj,nCell);
	}
}


/********************************************************************************
* 기      능   : 데이터Set 추가
********************************************************************************/
function fn_ds_list_OnRowInserted(obj,nRow,nCount)
{
	ds_list.SetColumn(nRow, "FLAG",  "I");
	ds_list.SetColumn(nRow, "DEPT",  cb_dept.Value);
	ds_list.SetColumn(nRow, "GJAHR", cb_gjahr.Value);
	ds_list.SetColumn(nRow, "WAERK", "KRW");	// 원화Setting
}

/********************************************************************************
* 기      능   : 입력/수정의 항목의 필드 필수 확인 
********************************************************************************/
function fn_dataSetIsNullCheck(obj, nRow, colNm) {
	if (length(trim(obj.GetColumn(nRow, colNm))) == 0)	{
		return false;		
	}

	return true;
}

/********************************************************************************
* 기      능   : 적용년월의 정합성 확인
********************************************************************************/
function fn_planYMCheck(obj, nRow, colNm)	{

	var vPlanYM = obj.GetColumn(nRow, colNm);
	if (length(trim(vPlanYM)) != 6)	{
		var arr = [nRow+1, "적용년월"];
		gfn_showMsg("CW00055",arr);
		fn_SetFocusGrid(grd_list, nRow, gfn_getColIdx(grd_list,colNm));
		return false;
	}

	var vPlanY = left(vPlanYM,  4);
	var vPlanM = right(vPlanYM, 2);

	if(length(vPlanY) != 4)	{
		var arr = [nRow+1, "적용년월"];
		gfn_showMsg("CW00055",arr);
		fn_SetFocusGrid(grd_list, nRow, gfn_getColIdx(grd_list,colNm));
		return false;
	}

	if (parseInt(vPlanY) > parseInt(cb_gjahr.Value))	{
		gfn_showMsg((nRow+1) + "번째 " + "적용년월은 기준년도보다 클 수 없습니다.");
		fn_SetFocusGrid(grd_list, nRow, gfn_getColIdx(grd_list,colNm));
		return false;
	}

	if(parseInt(vPlanM) < 1 || parseInt(vPlanM) > 12)	{
		var arr = [nRow+1, "적용년월"];
		gfn_showMsg("CW00055",arr);
		fn_SetFocusGrid(grd_list, nRow, gfn_getColIdx(grd_list,colNm));
		return false;
	}
	return true;
}

/********************************************************************************
* 기      능   : 계획금액 입력정보 확인
********************************************************************************/
function fn_planAmtCheck(obj, nRow) {
	// 계획년월 포함하여 12개월 입력
	// 적용년월이 기준년도와 동일한 년도인경우 적용년월 이후	
	var vMonth = "";

	//*===================이전계획 추가 관련 수정===========================*//
	//var vPlanY = left(obj.GetColumn(nRow,  "PLANYM"), 4);
	//var vPlanM = right(obj.GetColumn(nRow, "PLANYM"), 2);
	var vPlanY = "";
	var vPlanM = "";

    if ( obj.GetColumn(nRow,  "GUBUN") == 'A' )
    {
//alert("a");    
		vPlanY = left(obj.GetColumn(nRow,  "STYM"), 4);
		vPlanM = right(obj.GetColumn(nRow, "STYM"), 2); 
//alert(parseInt(vPlanY));		
//alert(parseInt(vPlanM));
    }
    else
    {

		vPlanY = left(obj.GetColumn(nRow,  "PLANYM"), 4);
		vPlanM = right(obj.GetColumn(nRow, "PLANYM"), 2);    
    }



//alert( "A : " + (parseInt(cb_gjahr.Value) - parseInt(vPlanY)));
//alert( "B : " + parseInt(cb_gjahr.Value) );
//alert( "C : " + parseInt(vPlanY) );
    var vchk = "";
    var n = 0;
	if ((parseInt(cb_gjahr.Value) - parseInt(vPlanY)) == 0 )	{	// 동일년도
		// 계획년월 이전에는 계획금액 불필요

		for (var i = 1; i <= 12; i++)	{
			vMonth = "M" + lpad(i, "0", 2);
			if (i < parseInt(vPlanM))	{

				if (length(trim(obj.GetColumn(nRow, vMonth))) > 0)	{
					gfn_showMsg((nRow+1) + "번째 " + i + "월의 계획금액은 불필요합니다.");
					fn_SetFocusGrid(grd_list, nRow, gfn_getColIdx(grd_list, vMonth));
					return false;
				}
			}else	{

				if (length(trim(obj.GetColumn(nRow, vMonth))) == 0)	{	
					n = "M" + lpad(i, "0", 2);
					//gfn_showMsg((nRow+1) + "번째 " + i + "월의 계획금액을 입력하세요.");
					//fn_SetFocusGrid(grd_list, nRow, gfn_getColIdx(grd_list, vMonth));
					//return false;
				}
				else {	vchk = "X";  } //계획년월 이후부터 한달이라도 금액이 있으면 통과


				if (toNumber(obj.GetColumn(nRow, vMonth)) <= 0)	{
					if (cb_dept.Value != F_DEPT)	// 구매부인상 제외 2013.07.11
					{	
						n = "M" + lpad(i, "0", 2);
						//gfn_showMsg((nRow+1) + "번째 " + i + "월의 계획금액을 입력하세요.");
						//fn_SetFocusGrid(grd_list, nRow, gfn_getColIdx(grd_list, vMonth));
						//return false;
					}
					else {	vchk = "X";  } //계획년월 이후부터 한달이라도 금액이 있으면 통과
				}

//alert( "i : " + i + "    vchk : " + vchk );  				

				if ( i == 12 && vchk != "X" )
				{
					gfn_showMsg("계획년월 이후부터 " + i + "월까지 계획금액이 한건도 존재하지 않습니다.");
					fn_SetFocusGrid(grd_list, nRow, gfn_getColIdx(grd_list, n));
					return false;
				}


			}
		}
	}else if ((parseInt(cb_gjahr.Value) - parseInt(vPlanY)) == 1 )	{	// 1년전 이전년도
//alert("F");
		for (var i = 1; i <= 12; i++)	{
			vMonth = "M" + lpad(i, "0", 2);
			if (i < parseInt(vPlanM))	{
//alert("G");			
				if (length(trim(obj.GetColumn(nRow, vMonth))) == 0)	{
					n = "M" + lpad(i, "0", 2);
					//gfn_showMsg((nRow+1) + "번째 " + i + "월의 계획금액을 입력하세요.");
					//fn_SetFocusGrid(grd_list, nRow, gfn_getColIdx(grd_list, vMonth));
					//return false;
				}
				else {	vchk = "X";  } //계획년월 이후부터 한달이라도 금액이 있으면 통과
//alert("H");
				if (toNumber(obj.GetColumn(nRow, vMonth)) <= 0)	{
					if (cb_dept.Value != F_DEPT)	{	// 구매부인상 제외 2013.07.11
						n = "M" + lpad(i, "0", 2);
						//gfn_showMsg((nRow+1) + "번째 " + i + "월의 계획금액을 입력하세요.");
						//fn_SetFocusGrid(grd_list, nRow, gfn_getColIdx(grd_list, vMonth));
						//return false;
					}
					else {	vchk = "X";  } //계획년월 이후부터 한달이라도 금액이 있으면 통과
				}
//alert(parseInt(vPlanM));			
//alert( "i : " + i + "    vchk : " + vchk );  

				if ( i == (parseInt(vPlanM) - 1) && vchk != "X" )
				{
					gfn_showMsg("1월 이후부터 " + (parseInt(vPlanM) - 1) + "월까지 계획금액이 한건도 존재하지 않습니다.");
					fn_SetFocusGrid(grd_list, nRow, gfn_getColIdx(grd_list, n));
					return false;
				}



			}else	{
//alert("I");			
				if (length(trim(obj.GetColumn(nRow, vMonth))) > 0)	{
					gfn_showMsg((nRow+1) + "번째 " + i + "월의 계획금액은 불필요합니다.");
					fn_SetFocusGrid(grd_list, nRow, gfn_getColIdx(grd_list, vMonth));
					return false;
				}
			}
		}
	}else	{
//alert("J");
		for (var i = 1; i <= 12; i++)	{
			vMonth = "M" + lpad(i, "0", 2);
			if (length(trim(obj.GetColumn(nRow, vMonth))) > 0)	{
				gfn_showMsg((nRow+1) + "번째 " + i + "월의 계획금액은 불필요합니다.");
				fn_SetFocusGrid(grd_list, nRow, gfn_getColIdx(grd_list, vMonth));
				return false;
			}
		}	
	}

	return true;
}

/********************************************************************************
* 기      능   : 확정 후 입력 시 계획금액 입력정보 확인
********************************************************************************/
function fn_planAmtNonCheck(obj, nRow) {
	// 계획년월 포함하여 12개월 입력
	// 적용년월이 기준년도와 동일한 년도인경우 적용년월 이후	
	var vMonth = "";

	for (var i = 1; i <= 12; i++)	{
		vMonth = "M" + lpad(i, "0", 2);
		if (length(trim(obj.GetColumn(nRow, vMonth))) > 0)	{
			gfn_showMsg((nRow+1) + "번째 " + i + "월의 계획금액은 불필요합니다.");
			fn_SetFocusGrid(grd_list, nRow, gfn_getColIdx(grd_list, vMonth));
			return false;
		}
	}	

	return true;
}

/********************************************************************************
* 기      능   : 정상적으로 등록된 항목에 대하여 도면/자재등록 가능
********************************************************************************/
function fn_grd_list_OnCellClick(obj,nRow,nCell,nX,nY,nPivotIndex)
{
	if (ds_list.GetRowCount() == 0 || nRow < 0)	return;

	if (length(trim(ds_list.GetColumn(nRow, "FLAG"))) == 0)	{
		btn_part.Enable    = true;
		btn_pdmcase.Enable = true;
	}else	{
		btn_part.Enable    = false;
		btn_pdmcase.Enable = false;
	}

	// 확정시에는 계획금액필드는 입력 불가
	var vMonth = "";
	for (var i = 1; i <= 12; i++)	{
		vMonth = "M" + lpad(i, "0", 2);
		if (F_CONFDT != "00000000")		{	// 확정 시
			if (ds_list.GetColumn(nRow, "FLAG") == "I")	{
				grd_list.SetCellProp("body", gfn_getColIdx(grd_list, vMonth), "edit", "none");
			}else	{
				grd_list.SetCellProp("body", gfn_getColIdx(grd_list, vMonth), "edit", "number");
			}
		}else	{
			grd_list.SetCellProp("body", gfn_getColIdx(grd_list, vMonth), "edit", "number");
		}
	}

	// 승인 및 권한에 따른 수정여부 확인
	if (F_CONFDT != "00000000")		{
		if (F_ADMIN_YN != "Y")	{
			if (ds_list.GetColumn(nRow, "FLAG") == "I")		{	// 입력의 경우 수정 가능
				grd_list.Editable = true;
			}else	{
				grd_list.Editable = false;
			}
		}else	{
			grd_list.Editable = true;
		}
	}
}

/********************************************************************************
* 기      능   : 항목 수정
********************************************************************************/
function fn_ds_list_OnColumnChanged(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex)
{
	if (ds_list.getColumn(nRow, "FLAG") != "I")	{
		if (strColumnID != "FLAG")	{
			if (varOldValue != varNewValue)		{
				ds_list.SetColumn(nRow, "FLAG",  "U");
			}
		}
	}
}

/********************************************************************************
* 기      능   : 이전항목등록 call
********************************************************************************/
function fn_btn_pre_plan_OnClick(obj)
{
	//년도필드 필수체크	
	if(gfn_isNull(cb_gjahr, true, st_gjahr.value))
		return;

	if(gfn_isNull(cb_dept, true, st_dept.value))
		return;

	if (F_QUERY_YN != "Y")	{
		gfn_showMsg("CW00054");
		return;
	}

	var arg = "P_GJAHR="   + quote(cb_gjahr.Value) + 
			  " P_DEPT="   + quote(cb_dept.Value);

	gfn_openPopup("QM0706001B", true, arg);
}

