/********************************************************************************
* 기      능   : Grid 포커스
********************************************************************************/
function fn_SetFocusGrid(pObjGrd, pRowNum, pColNum)  
{   
	var ObjDataset =  object(pObjGrd.BindDataset);

	pObjGrd.SetFocus();
	pObjGrd.SetCellPos(pColNum);

	ObjDataset.Fireevent 	= false; 
	ObjDataset.row 			= pRowNum;
	ObjDataset.Fireevent 	= true;
}

/********************************************************************************
* 기      능   : 부서정보 변경 시
********************************************************************************/
function fn_cb_dept_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
	if(length(trim(cb_gjahr.Value)) > 0 && length(trim(strCode)) > 0)	{
		fn_checkConfdate();
	}
}

/********************************************************************************
* 기      능   : 확정일자 정보 조회
********************************************************************************/
function fn_checkConfdate()	{
	// 확정(승인) 여부 확인
	tit_clearActionInfo();
	tit_addSearchActionInfo("qm07:QM0706001A_S2");
	tit_callService(
		  ""
		, ""
		, "ds_cond=ds_cond"
		, "ds_cnt=ds_cnt"
		, ""
		, "fn_afterCheckConfdate"
		, true);
}

/********************************************************************************
* 기      능   : 확정일자 정보 조회 callback
********************************************************************************/
function fn_afterCheckConfdate(errCode, errMsg) 	{
	if(ds_cnt.GetRowCount() > 0 && ds_cnt.GetColumn(0, "CNT") > 0) {
		// 확정일자 재 확인
		F_CONFDT = iif(length(trim(ds_cnt.GetColumn(0, "CONFDT"))) == 0, "00000000", ds_cnt.GetColumn(0, "CONFDT"));
	}else	{
		F_CONFDT = "00000000";
	}
}

/********************************************************************************
* 기      능   : 기준년도 변경 시
********************************************************************************/
function fn_cb_gjahr_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
	if(length(trim(strCode)) > 0 && length(trim(cb_dept.Value)) > 0)	{
		fn_checkConfdate();
	}
}
