#include "LIB::commons.js";	// 공통 스크립트 
//=========================================================================================
// [ PART 1 ]
// Form에서 사용될 전역변수를 선언
// FORM에서 사용되는 전역변수는 대문자로 작성하며, F_XXX 의 형태를 갖도록 한다. 
//=========================================================================================
var F_ADMIN_DEPT = "F2,F6";	// 효율개선부 -> 총무부로 변경(조직개편2016.04), 정보기술실(관리부서)		
var F_ADMIN_YN   = "Y";		// 관리부서 여부
var F_CALLYN     = "N";		// 원가상승실적집계 화면에서 호출여부

//=========================================================================================
// [ PART 2 ]
// Form Loading / Unloading 시 작업 정의
//=========================================================================================
function fn_OnLoadCompleted(obj) {

	// Form 로드 이벤트 맨 첫줄에 기술 
	gfn_initForm(true);

	cal_rsltym.Value = gfn_getCurrDate();
	cal_rsltym.SetFocus();

	// 관리부서가 아닌경우 부서정보 변경 불가
	if (IndexOf(F_ADMIN_DEPT, G_DPT1) == -1)		{
		F_ADMIN_YN = "N";
	}

	// 추후 정보 확인 후 삭제 필요 &&&&
	if (length(trim(G_DPT1)) == 0)	{
		F_ADMIN_YN = "Y";
	}

	// 부서정보  활성화
	if (F_ADMIN_YN == "Y")		{
		cb_dept.Enable      = true;
		cb_dept.Value       = "";
	}else	{
		cb_dept.Enable      = true;
	}

	if(F_ADMIN_YN == "Y")
	{
		// 원가절감 부서정보 조회
		var codeArr    = ["QM01"];
		var dsObjArr   = [ds_dept_cd];
		var addTypeArr = [G_CODE_NONE];
		gfn_searchCommCode(codeArr, dsObjArr, addTypeArr);
	}
	else
	{
		// 로그인 사용자별 권한에 따른 부서정보 조회
		tit_clearActionInfo();
		tit_addSearchActionInfo("qm07:QM0701001A_S4");

		tit_callService(
			  ""
			, ""
			, "ds_cond=ds_cond"
			, "ds_dept_cd=ds_dept_cd"
			, ""
			, "fn_completeInit"
			, true);
	}
}

/********************************************************************************
* 기      능   : form UnloadCompleted 
********************************************************************************/
function fn_OnUnloadCompleted(obj) {
	return gfn_isFormUnload(obj);
}

/********************************************************************************
* 기      능   : 조회 (header 조회)
********************************************************************************/
function fn_query() {

	gfn_showMsg("");

	//년도필드 필수체크	
	if(gfn_isNull(cal_rsltym, true, st_rsltym.value))
		return;

	// 기준년도 setting
	ds_cond.SetColumn(0, "GJAHR", SubStr(cal_rsltym.Value, 0, 4));

	// 실적년월 setting
	ds_cond.SetColumn(0, "RSLTYM", SubStr(cal_rsltym.Value, 0, 6));

	if(gfn_isNull(cb_dept, true, st_dept.value))
		return;

	ed_confyn.Text = "N";

	// HTTP SESSION TIMEOUT을 변경
	//http.TimeOut = 1800;

	tit_clearActionInfo();
	tit_addSearchActionInfo("qm07:QM0707002A_S1");

	tit_callService(
          ""
        , ""
        , "ds_cond=ds_cond"
        , "ds_list=ds_list"
        , ""
        , "fn_afterQuery"
        , true);
}

/********************************************************************************
* 기      능   : 조회 Callback  
********************************************************************************/
function fn_afterQuery(errCode, errMsg) {

	gfn_showSearchMsg(grd_list, errCode, errMsg);
	//gfn_showMsg("CI00002",ds_list.RowCount()+"");

	// 확정여부 조회
	tit_clearActionInfo();
	tit_addSearchActionInfo("qm07:QM0707002A_S2");

	tit_callService(
          ""
        , ""
        , "ds_cond=ds_cond"
        , "ds_conf=ds_list"
        , ""
        , "fn_afterConfYNQuery"
        , true);
}

/********************************************************************************
* 기      능   : 확정여부 조회 Callback  
********************************************************************************/
function fn_afterConfYNQuery(errCode, errMsg) {

	// 확정 정보를 확인하여 처리
	var vConfDate = ds_conf.GetColumn(0, "CFDATE");
	if (ds_conf.GetRowCount() > 0)	{
		if(vConfDate == null || replace(vConfDate, "00000000", "") == "")	{
			ed_confyn.Text = "N";
		}else	{
			ed_confyn.Text = "Y";
		}
	}else	{
		ed_confyn.Text = "N";
	}

	if (ed_confyn.Text == "Y")	{
		grd_list.Editable = false;
	}else	{
		grd_list.Editable = true;
	}

	// org 정보 저장
	ds_list_org.Copy("ds_list");
}

/********************************************************************************
* 기      능   : 공통코드가 조회된 후의 Callback 함수 
********************************************************************************/
function fn_completeInit(sCode, sDsObj, sCodeType) {
   ds_dept_cd.Filter("CODE<>'CK'");
//========================임시로 설계부만 기간정하여 관리자권한 부여========================
if ( substr(gfn_getCurrDate(),0,8) >= '20150105' && substr(gfn_getCurrDate(),0,8) <= '20150107' )
{
	F_ADMIN_DEPT = "F7,F6,C1,CB,CO";	// 효율개선부, 정보기술실(관리부서)+ 설계1,2,3분	
}
//============================================================================================

	cb_dept.Value = G_DPT1;


	// 원가상승실적집계에서 호출된 경우 처리
	if (isExistVar("P_RSLTYM", true))	{
		cal_rsltym.Value = P_RSLTYM;
		F_CALLYN = "Y";
	}

	if (isExistVar("P_DEPT", true))	{
		cb_dept.Value = P_DEPT;
		F_CALLYN = "Y";
	}

	if (F_CALLYN == "Y")	{
		fn_query();
	}
}

