	}
}

/********************************************************************************
* 기      능   : 입력/수정의 항목의 필드 필수 확인 
********************************************************************************/
function fn_dataSetIsNullCheck(obj, nRow, colNm) {
	// 상승후 금액은 0 이 가능함
	if (colNm == "BFAMT" || colNm == "AFAMT")	{
		if (length(trim(obj.GetColumn(nRow, colNm))) == 0)	{
			obj.SetColumn(nRow, colNm, 0);
		}
	}
	else	{
		if (length(trim(obj.GetColumn(nRow, colNm))) == 0)	{
			return false;
		}
	}

	return true;
}


/********************************************************************************
* 기      능   : 항목 수정
********************************************************************************/
function fn_ds_list_OnColumnChanged(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex)
{
	ds_list.onColumnChanged = "";
//alert("aaa");
	if (strColumnID != "FLAG")	{
//alert("bbb");
		var vOldValue = varOldValue;
		var vNewValue = varNewValue;

		if (strColumnID == "BFAMT" || strColumnID == "AFAMT" || strColumnID == "QNTY" || strColumnID == "SVAMT")	{

			// null에서 0으로 입력된 경우
			if(length(trim(varOldValue)) == 0 && toNumber(varNewValue) == 0)	{
				ds_list.onColumnChanged = "fn_ds_list_OnColumnChanged";
				return;
			}

			if (length(trim(ds_list.GetColumn(nRow, "KGBN"))) == 0)	{
				ds_list.SetColumn(nRow, "KGBN", "U");	// U ; 단가기준, T : 총액기준
			}

			vOldValue = toNumber(varOldValue);
			vNewValue = toNumber(varNewValue);
		}

		if (vOldValue != vNewValue)		{
			ds_list.SetColumn(nRow, "FLAG", "U");

			if (length(trim(ds_list.GetColumn(nRow, "SGBN"))) == 0)		{
				ds_list.SetColumn(nRow, "SGBN", "M");	// 수기등록
			}


			if(strColumnID == "BFAMT" || strColumnID == "AFAMT" || strColumnID == "QNTY" || strColumnID == "KGBN")	{
				var vBfamt = ds_list.GetColumn(nRow, "BFAMT");
				var vAfamt = ds_list.GetColumn(nRow, "AFAMT");
				var vQnty  = ds_list.GetColumn(nRow, "QNTY");

				var vKgbn = ds_list.GetColumn(nRow, "KGBN");
				if (vKgbn == "T")	{	// 단가기준에서 총액기준으로 변경
					//ds_list.SetColumn(nRow, "RSLTAMT", toNumber(vBfamt) - toNumber(vAfamt));
					//ds_list.SetColumn(nRow, "SVAMT",   (toNumber(vBfamt) - toNumber(vAfamt)) / iif(toNumber(vQnty) == 0, 1, toNumber(vQnty)) );
					ds_list.SetColumn(nRow, "RSLTAMT", toNumber(vAfamt) - toNumber(vBfamt));
//alert(toNumber(vAfamt));					
//alert(toNumber(vBfamt));
					ds_list.SetColumn(nRow, "SVAMT",   (toNumber(vAfamt) - toNumber(vBfamt)) / iif(toNumber(vQnty) == 0, 1, toNumber(vQnty)) );
				}else if (vKgbn == "U")		{	// 총액기준에서 단가기준으로 변경
					//ds_list.SetColumn(nRow, "SVAMT",   toNumber(vBfamt) - toNumber(vAfamt));
					//ds_list.SetColumn(nRow, "RSLTAMT", (toNumber(vBfamt) - toNumber(vAfamt)) * toNumber(vQnty));
//alert(toNumber(vAfamt));					
//alert(toNumber(vBfamt));					
					ds_list.SetColumn(nRow, "SVAMT",   toNumber(vAfamt) - toNumber(vBfamt));
					ds_list.SetColumn(nRow, "RSLTAMT", (toNumber(vAfamt) - toNumber(vBfamt)) * toNumber(vQnty));
				}
			}

			if(strColumnID == "SVAMT")	{

				var vSvamt = ds_list.GetColumn(nRow, "SVAMT");
				var vQnty  = ds_list.GetColumn(nRow, "QNTY");

				var vKgbn = ds_list.GetColumn(nRow, "KGBN");
				if (vKgbn == "T")	{	// 단가기준에서 총액기준으로 변경
					ds_list.SetColumn(nRow, "RSLTAMT", toNumber(vSvamt));
				}else if (vKgbn == "U")		{	// 총액기준에서 단가기준으로 변경
					ds_list.SetColumn(nRow, "RSLTAMT", toNumber(vSvamt) * toNumber(vQnty));
				}
			}

		}
	}
	ds_list.onColumnChanged = "fn_ds_list_OnColumnChanged";
}


/********************************************************************************
* 기      능   : Grid 포커스
********************************************************************************/
function fn_SetFocusGrid(pObjGrd, pRowNum, pColNum)  
{   
	var ObjDataset =  object(pObjGrd.BindDataset);

	pObjGrd.SetFocus();
	pObjGrd.SetCellPos(pColNum);

	ObjDataset.Fireevent 	= false; 
	ObjDataset.row 			= pRowNum;
	ObjDataset.Fireevent 	= true;
}

/********************************************************************************
* 기      능   : 항목 더블클릭 시 시스템으로 실적이 집계된 정보 호출
********************************************************************************/
function fn_grd_list_OnCellDblClick(obj,nRow,nCell,nX,nY,nPivotIndex)
{
	if (nCell == gfn_getColIdx(grd_list,"ITEM") || nCell == gfn_getColIdx(grd_list,"ITEMNM"))	{

		if (ds_list.GetColumn(nRow, "SGBN") != "S")		{
			var vMsg = "도면/자재실적 상세내역";
			gfn_showAlert("CW00113", vMsg);
			return;
		}


		// 원가상승 도면/자재 실적내역 호출
		var arg = "P_GJAHR="   + quote(ds_list.GetColumn(nRow, "GJAHR")) + 
				  " P_RSLTYM=" + quote(ds_list.GetColumn(nRow, "RSLTYM")) + 
				  " P_DEPT="   + quote(ds_list.GetColumn(nRow, "DEPT"))   +
				  " P_ITEM="   + quote(ds_list.GetColumn(nRow, "ITEM"));

		gfn_openPopup("QM0707002B", true, arg);
	}
}

/********************************************************************************
* 기      능   : 도면/자재 실적 내역 조회 화면 호출
********************************************************************************/
