//=========================================================================================
// [ PART 2 ]
// Form Loading / Unloading 시 작업 정의
//=========================================================================================
function fn_OnLoadCompleted(obj) {

	gfn_initForm(true);	
	fn_init_form();	// 폼 초기화
}

/********************************************************************************
* 기      능   : form UnloadCompleted 
********************************************************************************/
function fn_OnUnloadCompleted(obj) {
	return gfn_isFormUnload(obj);
}


/********************************************************************************
* 기      능   : 폼 초기화
********************************************************************************/
function fn_init_form() {

	cal_qtdat_fm.Text = addDate(gfn_getCurrDate(), -5);	// 현재일자 5일전의 일자부터
	cal_qtdat_to.Text = gfn_getCurrDate();

	gfn_BindColumn(div_detail.div_pur.ed_sman_cd, "ds_list", "PURMAN"); 	// 구매담당자코드 데이타셋 바인드
	gfn_BindColumn(div_detail.div_pur.ed_sman_nm, "ds_list", "PURMAN_NM"); 	// 구매담당자 데이타셋 바인드
	gfn_BindColumn(div_detail.div_qc.ed_sman_cd, "ds_list", "QCMAN"); 	// 품질담당자코드 데이타셋 바인드
	gfn_BindColumn(div_detail.div_qc.ed_sman_nm, "ds_list", "QCMAN_NM"); 	// 품질담당자 데이타셋 바인드

	//div_detail.div_pur.ed_sman_nm.enable = false;								// 대리점명 수정불가


	if(G_VEND_CODE.substr(0,1) <> "0") //접속자가 협력사일 경우
	{
		div_sman.btn_popLifnr.enable = false;
		div_sman.ed_lifnr.enable = false;
		div_sman.ed_name1.enable = false;
		btn_request.Enable = false;
		btn_result.Enable = false;
		btn_register.Enable = false;
		btn_reject.Enable = false;

		div_sman.ed_lifnr.Text = G_VEND_CODE_M;
		div_sman.ed_name1.Text = G_VEND_NAME;
		div_detail.ed_lifnrName.Text = G_VEND_NAME;	

		div_detail.cal_inspectdat.enable = false;		
	}	
	else if (G_VEND_CODE == "0000009920") //접속자가 구매부서일 경우
	{
		btn_register.Enable = false;
		btn_reject.Enable = false;
		btn_confirm.Enable = false;

		div_detail.cal_qtdat.enable = false;
		div_detail.ed_prod.enable = false;
		div_detail.ed_reqman.enable = false;
		div_detail.div_pur.ed_sman_cd.enable = false;
		div_detail.div_pur.ed_sman_nm.enable = false;
		div_detail.div_pur.btn_popSman.enable = false;
		div_detail.div_qc.ed_sman_cd.enable = false;
		div_detail.div_qc.ed_sman_nm.enable = false;
		div_detail.div_qc.btn_popSman.enable = false;
		div_detail.cal_inspectdat.enable = false;	

		div_pur.ed_sman_cd.Text = G_USER_ID;
		div_pur.ed_sman_nm.Text = G_USER_NAME;
		//div_pur.ed_sman_cd.enable = false;
		//div_pur.ed_sman_nm.enable = false;
		//div_pur.btn_popSman.enable = false;
	}
	else if (G_VEND_CODE == "0000009936") //접속자가 품질부서일 경우
	{
		btn_request.Enable = false;
		btn_result.Enable = false;
		btn_confirm.Enable = false;

		div_detail.cal_qtdat.enable = false;
		div_detail.ed_prod.enable = false;
		div_detail.ed_reqman.enable = false;
		div_detail.div_pur.ed_sman_cd.enable = false;
		div_detail.div_pur.ed_sman_nm.enable = false;
		div_detail.div_pur.btn_popSman.enable = false;
		div_detail.div_qc.ed_sman_cd.enable = false;
		div_detail.div_qc.ed_sman_nm.enable = false;
		div_detail.div_qc.btn_popSman.enable = false;
		div_detail.cal_inspectdat.enable = false;

		div_qc.ed_sman_cd.Text = G_USER_ID;
		div_qc.ed_sman_nm.Text = G_USER_NAME;
		//div_qc.ed_sman_cd.enable = false;
		//div_qc.ed_sman_nm.enable = false;
		//div_qc.btn_popSman.enable = false;
	}
	else
	{
		//btn_request.Visible = false;
		//btn_result.Visible = false;
		//btn_register.Visible = false;
		//btn_reject.Visible = false;
		//btn_confirm.Visible = false;	
		//btn_request.Enable = false;
		//btn_result.Enable = false;
		//btn_confirm.Enable = false;

		//div_detail.cal_qtdat.enable = false;
		//div_detail.ed_prod.enable = false;
		//div_detail.ed_reqman.enable = false;
		//div_detail.cb_ekgrp.enable = false;
		//div_detail.cb_qcman.enable = false;
	}	

}

//=========================================================================================
// [ PART 3 ]
// Main Process Event 정의(조회,입력,저장,form닫기)
//=========================================================================================
function fn_query() { 

	ds_cond.ClearData();
	ds_cond.AddRow();

	ds_cond.SetColumn(0, "FR_QTDAT",  cal_qtdat_fm.Value);
	ds_cond.SetColumn(0, "TO_QTDAT",  cal_qtdat_to.Value);
	ds_cond.setColumn(0, "LIFNR", div_sman.ed_lifnr.Text);
	ds_cond.setColumn(0, "ZRQSTA", cb_zrqsta.Value);
	ds_cond.setColumn(0, "PURMAN", div_pur.ed_sman_cd.Text);
	ds_cond.setColumn(0, "QCMAN", div_qc.ed_sman_cd.Text);

	nsfRequest(  "search"
			   , "sag0990/find"
			   , "ds_cond=ds_cond ds_list=ds_list ds_file=ds_file"
			   , "ds_list=ds_list ds_file=ds_file ds_error=ds_error"
			   , ""
			   , "fn_afterQuery"
			  );  	
}

/********************************************************************************
* 기      능   : 조회 2
********************************************************************************/
function fn_detail() {

	ds_error.ClearData();
	ds_cond.ClearData();
	ds_cond.AddRow();

	ds_cond.SetColumn(0, "ZRQSEQ", grd_list.GetCellText("Body", grd_list.CurrentRow, 1));

	if(grd_list.GetCellText("Body", grd_list.CurrentRow, 1)!="")
	{
		ds_file.ClearData();
	}

	nsfRequest( "detail"
			  , "sag0990/findListDetail"
			  , "ds_cond=ds_cond ds_file=ds_file"
			  , "ds_file=ds_file ds_error=ds_error"
			  , ""
			  , ""
			  );	
}

/********************************************************************************
* 기      능  : 입력 : 맨뒤에 행추가하고 상세정보 항목 초기화
********************************************************************************/
function fn_input() {

	if (G_VEND_CODE == "0000009920") //접속자가 구매부서일 경우
	{
		gfn_showAlert("ZW00003");
		return false; 
	}
	else if (G_VEND_CODE == "0000009936") //접속자가 품질부서일 경우
	{
		gfn_showAlert("ZW00003");
		return false; 
	}

	var newRow = gfn_addGridRow(grd_list, -1, "FLAG"); // 행추가 (맨뒤에 추가됨:FLAG(I)) // 추가된 행번호를 받아옴

	/// 새로 추가된 행에 기본값을 설정한다.   
	ds_list.Fireevent = false;	

	ds_list.SetColumn(newRow, "ZRQDAT" , replace(gfn_getCurrDate(),".",""));
	ds_list.SetColumn(newRow, "ZRQSTA", "R" ); // 진행상태 검사의뢰-R, 검사의뢰확인-C, 검사정보등록-I, 결과공유-P, 반려-X	
	ds_list.SetColumn(newRow, "LIFNR", G_VEND_CODE_M);
	ds_list.SetColumn(newRow, "LIFNR_NM", G_VEND_NAME);
	ds_list.SetColumn(newRow, "REQMAN", G_USER_NAME);
	ds_list.SetColumn(newRow, "EMAIL", G_EMAIL);

	ds_list.Fireevent = true;		

	ds_file.ClearData();

}

/********************************************************************************
* 기      능   : 저장 : 등록, 수정
********************************************************************************/
function fn_save() { 

	if (G_VEND_CODE == "0000009920") //접속자가 구매부서일 경우
	{
		gfn_showAlert("ZW00003");
		return false; 
	}
	else if (G_VEND_CODE == "0000009936") //접속자가 품질부서일 경우
	{
		gfn_showAlert("ZW00003");
		return false; 
	}

	// 필수 체크
	var ZRQDAT   = div_detail.cal_qtdat.Text; 	// 요청일
	var REQMAN   = div_detail.ed_reqman.Text;	// 의뢰자
	var QCMAN   = div_detail.div_qc.ed_sman_nm.Text; 	// 품질담당자
	var PURMAN   = div_detail.div_pur.ed_sman_nm.Text;  	// 구매담당자
	var PRODC   = div_detail.ed_prod.Text; 	// 제품
	var ZATTPATH   = ds_file.GetColumn(0, "ZATTPATH"  ); 	// 파일경로

	if ( ZRQDAT   == null || ZRQDAT   == "" )
	{ 
		gfn_showAlert("CW00208", "의뢰일자" );
		div_detail.cal_qtdat.SetFocus(); 	// 의뢰일자에 강제 focus처리
		return false; 
	} // [${}]은/는 필수입력 입니다. 확인바랍니다.

    if ( PRODC   == null || PRODC   == "" ) 
    { 
		gfn_showAlert("CW00208", "제품명"); 
		div_detail.ed_prod.SetFocus(); 	// 제품명에 강제 focus처리
		return false; 
	} // [${}]은/는 필수입력 입니다. 확인바랍니다.

	if ( REQMAN    == null || REQMAN    == "" ) 
	{
		gfn_showAlert("CW00208", "의뢰자"); 
		div_detail.ed_reqman.SetFocus(); 	// 의뢰자에 강제 focus처리
		return false; 
	} // [${}]은/는 필수입력 입니다. 확인바랍니다.

	if ( PURMAN   == null || PURMAN   == "" ) 
	{ 
		gfn_showAlert("CW00208", "구매담당자"); 
		div_detail.div_pur.btn_popSman.SetFocus(); 	// 구매담당자에 강제 focus처리
		return false; 
	} // [${}]은/는 필수입력 입니다. 확인바랍니다.

	if ( QCMAN   == null || QCMAN   == "" ) 
	{
		gfn_showAlert("CW00208", "품질담당자"); 
		div_detail.div_qc.btn_popSman.SetFocus(); 	// 품질담당자에 강제 focus처리
		return false; 
	} // [${}]은/는 필수입력 입니다. 확인바랍니다.
	if ( ZATTPATH   == null || ZATTPATH   == "" )
	{
		gfn_showAlert("CW00208", "검사의뢰서");	
		btn_add.SetFocus(); 	// 파일추가버튼에 강제 focus처리	
		return false; 
	} // [${}]은/는 필수입력 입니다. 확인바랍니다.

    if ( !gfn_showConfirm("CI00024", "[저장]") ) { // [저장]을 하시겠습니까?
		return;
	}	

	var inDs =  "ds_list=ds_list"
      		 + " ds_file=ds_file"
	;
	var outDs = "ds_list=ds_list"
		     + " ds_file=ds_file"
			 + " ds_error=ds_error"
	;
	nsfRequest("save", "sag0990/save", inDs, outDs, "", "fn_query_saveAfter");

}

/********************************************************************************
* 기      능   : 액셀  
********************************************************************************/
function fn_excel() {

 	gfn_clearMsgBar();
	if (fn_valid_chk("excel") == false)  return; 	// 조회조건 항목오류검증
	gfn_saveToExcel(grd_list, true); 	// Excel Export  
}

/********************************************************************************
* 기      능   : 닫기  
********************************************************************************/
function fn_close() {

	if (fn_valid_chk("close") == false) return false;
		close();
}

//=========================================================================================
// [ PART 4]
// Callback 함수 정의 : callback 함수명은 메인 함수의 fn_xxxx() 에서 xxxx 부분은 fn_afterXXXX()
// 형태가 되도록 작성 
//=========================================================================================
/********************************************************************************
* 기      능   : 조회 Callback  
********************************************************************************/
function fn_afterQuery(errCode, errMsg) {  

	fn_detail();

} 

/********************************************************************************
* 기      능   : 저장 후 재조회 Callback  (협력사에서 구매/품질 부서에게 메일 전송)
********************************************************************************/
function fn_query_saveAfter(pCallback) { 

	fn_query();

	//=====================구매담당자에게 메일 전송====================
	var body;
	var title;
	var zkun_email;

	zkun_email = div_detail.div_pur.ed_sman_mail.Text;


	title = "초도품 검사 의뢰 요청";
	body = div_detail.ed_reqman.Text + "님의 의뢰가 요청되었습니다.\n확인 후 처리 바랍니다."
		+ "\n감사합니다.\n" ;
	body = body + "발송일 : " + gfn_getCurrDate();    

		tit_clearActionInfo();	
		tit_callService(
			"COMM_ACT"
			, "sendmail"
			, ""
			, ""
			//, "USER_JUMIN=" + quote(med_jumin1.value+med_jumin2.value)
			//	+ 
			," SEND_USER=ADMIN@HDEL.CO.KR"
				//+ " RECE_USER=SK.KANG@HDEL.CO.KR"
				+ " RECE_USER=" + quote( zkun_email )  
				+ " MESGTITL=" + quote( title ) 
				+ " MESGBODY=" + quote( body ) 
			, ""  //"fn_afterQuery"
			, false
			, true);		

	//=====================품질담당자에게 메일 전송====================
	var body;
	var title;
	var zkun_email;

	zkun_email = div_detail.div_qc.ed_sman_mail.Text;

	title = "초도품 검사 의뢰 요청";
	body = div_detail.ed_reqman.Text + "님의 의뢰가 요청되었습니다.\n확인 후 처리 바랍니다."
		+ "\n감사합니다.\n" ;
	body = body + "발송일 : " + gfn_getCurrDate();    

		tit_clearActionInfo();	
		tit_callService(
			"COMM_ACT"
			, "sendmail"
			, ""
			, ""
			//, "USER_JUMIN=" + quote(med_jumin1.value+med_jumin2.value)
			//	+ 
			," SEND_USER=ADMIN@HDEL.CO.KR"
				//+ " RECE_USER=SK.KANG@HDEL.CO.KR"
				+ " RECE_USER=" + quote( zkun_email )  
				+ " MESGTITL=" + quote( title ) 
				+ " MESGBODY=" + quote( body ) 
			, ""  //"fn_afterQuery"
			, false
			, true);				

}

