/********************************************************************************
* 기      능   : 첨부파일 추가
********************************************************************************/
function btn_add_OnClick(obj)
{		
	if(ds_list.GetColumn(ds_list.currow,"ZRQSTA")=="R" && G_VEND_CODE.substr(0,1) <> "0")
	{
		if( ds_file.GetRowCount() < 2)
		{
			var nRow = ds_file.AddRow();
			var nSeq = toNumber(ds_file.Max("ZATTSEQ"));
			if ( nSeq == "" || nSeq == null ) nSeq = 0;

			ds_file.setColumn(nRow, "FLAG"   , "I");
			ds_file.setColumn(nRow, "MANDT"  , ds_list.GetColumn(0, "MANDT" ));
			ds_file.setColumn(nRow, "ZRQSEQ" , ed_zrqseq.Text);
			ds_file.SetColumn(nRow, "ZATTSEQ", nSeq+1);	
			ds_file.SetColumn(nRow, "ZATGBN", "R"    ); //첨부파일 구분 의뢰서 첨부-R, 결과서 첨부-F	
		}		
		else
		{
			alert("첨부가능한 의뢰서의 수는 최대 2개입니다");
			return false;
		}		
	}

	else if(ds_list.GetColumn(ds_list.currow,"ZRQSTA")=="R" && G_VEND_CODE.substr(0,1) == "0")
	{
		alert("의뢰서를 첨부할 권한이 없습니다");
		return false;				
	}

	else if(ds_list.GetColumn(ds_list.currow,"ZRQSTA")=="C" && G_VEND_CODE == "0000009920")
	{
		alert("의뢰서를 첨부할 권한이 없습니다");
		return false;				
	}

	//else if(ds_list.GetColumn(ds_list.currow,"ZRQSTA")=="C" && G_VEND_CODE == "0000009936")
	else if(ds_list.GetColumn(ds_list.currow,"ZRQSTA")=="C")
	{		
		if( ds_file.GetRowCount() < 4 )
		{
			var nRow = ds_file.AddRow();

			ds_file.setColumn(nRow, "FLAG"   , "I");
			ds_file.setColumn(nRow, "MANDT"  , ds_list.GetColumn(0, "MANDT" ));
			ds_file.setColumn(nRow, "ZRQSEQ" , ed_zrqseq.Text);	
			ds_file.SetColumn(nRow, "ZATGBN", "F"    ); //첨부파일 구분 의뢰서 첨부-R, 결과서 첨부-F	
		}
		else
		{
			alert("첨부가능한 결과서의 수는 최대 2개입니다");
			return false;
		}	
	}		

	//else if(ds_list.GetColumn(ds_list.currow,"ZRQSTA")=="C" && G_VEND_CODE != "0000009936")
	//{		
		//alert("결과서를 첨부할 권한이 없습니다");
		//return false;				
	//}		

	else if(ds_list.GetColumn(ds_list.currow,"ZRQSTA")=="I")
	{		
		alert("검사정보등록 상태에서는 결과서를 첨부할 수 없습니다");
		return false;				
	}	

	else if(ds_list.GetColumn(ds_list.currow,"ZRQSTA")=="P")
	{		
		alert("검사정보확인 상태에서는 결과서를 첨부할 수 없습니다");
		return false;				
	}	
}

/********************************************************************************
* 기      능   : 첨부파일 삭제
********************************************************************************/
function btn_del_OnClick(obj)
{
	var nRow = ds_file.currow;
	if ( nRow > -1 ) {
		var sRowType = ds_file.GetRowType(nRow);
		if ( sRowType == "insert" ) {
			ds_file.DeleteRow(nRow);
		} else {
			ds_file.SetColumn(nRow, "FLAG", "D");
		}
	}
}

/********************************************************************************
* 기      능   : 새로고침
********************************************************************************/

function grd_list_OnHeadClick(obj,nCell,nX,nY,nPivotIndex) { // 그리드 헤더 클릭 시

	if      ( nCell == 0 ) gfn_selectGridCol(obj); 
	else if ( nCell > 1  ) gfn_sortGrid(obj, nCell);
}

function grd_list_OnKeyDown(obj,nChar,bShift,bCtrl,bAlt,nLLParam,nHLParam) { // 그리드에서 키 입력 시

	var ds = object(obj.BindDataset);  

	if (nChar==13) { // 엔터키
		ds.row = ds.row + 1;  // 다음 행으로 이동
	} else if (nChar==27)	{ // Esc 키이면 변경전 자료로 되돌린다. 
		var nCol 	= obj.GetCellPos();
		gfn_SetOrgColumn(obj, ds.row, nCol);  
	} else {
		gfn_GrdCellCopyPasteReset(obj, ds, bCtrl, nChar);
	} 
} 

function grd_file_OnExpandEdit(obj,nRow,nCell,strVal,nPivotIndex)
{
	if ( nCell == obj.GetBindCellIndex("body", "ZATTFN" ) ) {
		var arr = gfn_openFileMgr(
		      F_SERVER_DIR							// fa_serverDir
			, ds_file.getColumn(nRow, "ZATTFN")		// fa_serverFileName
			, ds_file.getColumn(nRow, "ZATTPATH")	// fa_localFileDir
			, ""									// fa_fileType
			, imgUploadFlg							// fa_saveType ("":업로드, "VIEW":보기)
			, true									// fa_isServerSave
			//, 									// fa_saveFileName
			//, false								// fa_isSave
			//, 									// fa_maxFileSize
		);

		if ( arr != null ) {
		   ds_file.setColumn(nRow, "ZATTPATH", arr[0]); // 디렉토리 정보 
		   ds_file.setColumn(nRow, "ZATTFN"  , arr[1]); // 파일명 
		}
	}
}

function ds_file_OnColumnChanged(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex)
{
	if ( varOldValue != varNewValue ) {
		if ( ds_file.GetColumn(nRow, "FLAG") != "I" && ds_file.GetColumn(nRow, "FLAG") != "D" ) {
			ds_file.SetColumn(nRow, "FLAG", "U");
		}
	}	
}

function grd_list_OnCellClick(obj,nRow,nCell,nX,nY,nPivotIndex)
{
	if (ds_list.GetRowCount() > 0)	
	{
		fn_detail();	
	}			
}

function grd_list_OnCellPosChanged(obj,nRow,nCell,nOldRow,nOldCell,nPivotIndex,nOldPivotIndex)
{
	if (nOldRow != nRow)	{
		grd_list_OnCellClick(obj, nRow, nCell, "", "", nPivotIndex);
	}	

	if(ds_list.GetColumn(nRow,"ZRQSTA") == "C")
	{
		div_detail.cal_inspectdat.Text = gfn_getCurrDate();	
	}

	//여기서 권한
	// if ( ds_list.GetColumn(nRow,"ZAT)
	// {
		// btn_request.Enable = false;
	// }
}

/********************************************************************************
* 기      능   : 검사의뢰확인
********************************************************************************/
function btn_request_OnClick(obj)
{
	if(ds_list.GetColumn(ds_list.currow,"ZRQSTA")!="R")
	{
		alert("진행상태가 검사의뢰 상태가 아닙니다");
		return false;
	}

	if ( !gfn_showConfirm("CI00024", "[검사의뢰확인]") ) { // [검사의뢰확인]을 하시겠습니까?
		return;
	}	

	if (ds_list.GetRowCount() > 0 && ds_list.GetColumn(ds_list.currow,"ZRQSTA")=="R")	{		
		ds_list.SetColumn(ds_list.currow, "ZRQSTA", "C" );				
	}else	{
		gfn_showAlert("CW00013");
	}

	var inDs =  "ds_list=ds_list"      	
	;
	var outDs = "ds_list=ds_list"
			 + " ds_error=ds_error"
	;

	nsfRequest("request", "sag0990/request", inDs, outDs, "", "fn_query_request_saveAfter");	
}

/********************************************************************************
* 기      능   : 검사정보등록
********************************************************************************/
function btn_register_OnClick(obj)
{
	if(ds_list.GetColumn(ds_list.currow,"ZRQSTA")!="C")
	{
		alert("진행상태가 검사의뢰확인 상태가 아닙니다");
		return false;
	}

	var TSTDT = div_detail.cal_inspectdat.Text; 	// 검사일자

	if ( TSTDT  == null || TSTDT   == "" ) 
	{
		gfn_showAlert("CW00208", "검사일자"); 
		div_detail.cal_inspectdat.SetFocus(); 	// 검사일자에 강제 focus처리
		return false; 
	} // [${}]은/는 필수입력 입니다. 확인바랍니다.

	if(grd_file.GetCellText("Body", grd_file.CurrentRow, 3)	!= "결과서 첨부")
	{
		gfn_showAlert("CW00208", "검사결과서");	
		btn_add.SetFocus(); 	// 파일추가버튼에 강제 focus처리	
		return false; 
	} // [${}]은/는 필수입력 입니다. 확인바랍니다.

	if ( !gfn_showConfirm("CI00024", "[검사정보등록]") ) { // [검사정보등록]을 하시겠습니까?
		return;
	}			

	if (ds_list.GetRowCount() > 0 && ds_list.GetColumn(ds_list.currow,"ZRQSTA")=="C")	{		
		ds_list.SetColumn(ds_list.currow, "ZRQSTA", "I" );				
	}else	{
		gfn_showAlert("CW00013");
	}

	var inDs =  "ds_list=ds_list"
      		 + " ds_file=ds_file"
	;
	var outDs = "ds_list=ds_list"
		     + " ds_file=ds_file"
			 + " ds_error=ds_error"
	;
	nsfRequest("register", "sag0990/register", inDs, outDs, "", "fn_query_register_saveAfter");
}

