//=========================================================================================
// [ PART 1 ]
// Form에서 사용될 전역변수를 선언
// FORM에서 사용되는 전역변수는 대문자로 작성하며, F_XXX 의 형태를 갖도록 한다. 
//=========================================================================================
var F_TITLE 		= "";  // 화면 타이틀
var F_SMAN_CD 		= "";  // 담당자코드
var F_SMAN_NM 		= "";  // 담당자명
var F_SMAN_FILTER	= "";  // 담당자필터구분

//=========================================================================================
// [ PART 2 ]
// Form Loading / Unloading 시 작업 정의
//=========================================================================================
function fn_OnLoadCompleted(obj) {

	gfn_initForm();		// Form 로드 이벤트 맨 첫줄에 기술 

	//F_TITLE = fa_frm_title;

	div_btn.fn_loadUseBtn("1,45", iif(length(F_TITLE)>0,F_TITLE,"담당자조회"), "", "");   	// 화면제목 설정 및 버튼 활성화

	// 폼 파라메터 체크 및 기본값 설정

	F_SMAN_CD = fa_sman_cd;  // 담당자 코드
	F_SMAN_NM = fa_sman_nm;  // 담당자 명

	ed_sman_cd.Text = F_SMAN_CD;  
	ed_sman_nm.Text = F_SMAN_NM; 

	// 자동 조회		
	if (length(trim(ed_sman_cd.Text)) > 0 || length(trim(ed_sman_nm.Text)) > 0 ) {
		fn_query(); // 임시주석처리
	}
}

/********************************************************************************
* 기      능   : 조회 (header 조회)
********************************************************************************/
function fn_query() { 
	if(ed_sman_cd.Text == "" && ed_sman_nm.Text == "")
	{		
		ds_list_sman.ClearData();
		for(var i = 0; i<ds_list_temp.GetRowCount(); i++)
		{
			var code = ds_list_temp.GetColumn(i,"SMAN_CD");
			var name = ds_list_temp.GetColumn(i,"SMAN_NM");
			var mail = ds_list_temp.GetColumn(i,"SMAN_MAIL");
			var nRow = ds_list_sman.AddRow();

			ds_list_sman.SetColumn(nRow, "SMAN_CD", code);
			ds_list_sman.SetColumn(nRow, "SMAN_NM", name);
			ds_list_sman.SetColumn(nRow, "SMAN_MAIL", mail);	
		}	
		ds_list_sman.row = 0;
	} 

	for(var i = 0; i<ds_list_temp.GetRowCount(); i++)
	{
		if(ed_sman_cd.Text == ds_list_temp.GetColumn(i,"SMAN_CD") && ed_sman_nm.Text == "")
		{		
			var code = ds_list_temp.GetColumn(i,"SMAN_CD");
			var name = ds_list_temp.GetColumn(i,"SMAN_NM");
			var mail = ds_list_temp.GetColumn(i,"SMAN_MAIL");

			ds_list_sman.ClearData();
			ds_list_sman.AddRow();		

			ds_list_sman.SetColumn(0, "SMAN_CD", code);
			ds_list_sman.SetColumn(0, "SMAN_NM", name);
			ds_list_sman.SetColumn(0, "SMAN_MAIL", mail);	
		}
	}	

	for(var i = 0; i<ds_list_temp.GetRowCount(); i++)
	{
		if(ed_sman_cd.Text == "" && ed_sman_nm.Text == ds_list_temp.GetColumn(i,"SMAN_NM"))
		{		
			var code = ds_list_temp.GetColumn(i,"SMAN_CD");
			var name = ds_list_temp.GetColumn(i,"SMAN_NM");
			var mail = ds_list_temp.GetColumn(i,"SMAN_MAIL");

			ds_list_sman.ClearData();
			ds_list_sman.AddRow();		

			ds_list_sman.SetColumn(0, "SMAN_CD", code);
			ds_list_sman.SetColumn(0, "SMAN_NM", name);
			ds_list_sman.SetColumn(0, "SMAN_MAIL", mail);							
		}	
	}

	for(var i = 0; i<ds_list_temp.GetRowCount(); i++)
	{
		if(ed_sman_cd.Text == ds_list_temp.GetColumn(i,"SMAN_CD") && ed_sman_nm.Text == ds_list_temp.GetColumn(i,"SMAN_NM"))
		{		
			var code = ds_list_temp.GetColumn(i,"SMAN_CD");
			var name = ds_list_temp.GetColumn(i,"SMAN_NM");
			var mail = ds_list_temp.GetColumn(i,"SMAN_MAIL");

			ds_list_sman.ClearData();
			ds_list_sman.AddRow();		

			ds_list_sman.SetColumn(0, "SMAN_CD", code);
			ds_list_sman.SetColumn(0, "SMAN_NM", name);
			ds_list_sman.SetColumn(0, "SMAN_MAIL", mail);							
		}
	}	
}
/********************************************************************************
* 기      능   : 조회 Callback  
********************************************************************************/
function fn_afterQuery(errCode, errMsg) { 

	// 조회된 자료가 있을 경우 1행에 포커스 위치시킨다.
	if (ds_list_sman.RowCount() > 0) {
		grd_list.SetFocus();
		trace("fn_Query SUCCESS!!! [건수 : " + ds_list_sman.RowCount() + "]");
	} else {
		trace("fn_Query SUCCESS!!! 건수없음");
		gfn_showMsg("CI00002", ds_list_sman.RowCount() + "");
	}

}

