	// Excel Export 
	gfn_saveToExcel(grd_list, true);
}

/********************************************************************************
* 기      능   : 확인버튼 클릭 시
********************************************************************************/
function fn_confirm() {

	if (ds_list_sman.GetColumn(ds_list_sman.row, "SMAN_MAIL") == "") //접속자의 메일이 없을 경우
	{
		alert("메일이 등록되지 않은 사용자입니다");
		return false; 
	}

	// 선택된 담당자코드, 담당자명을 배열에 담는다.
	var arr = [  ds_list_sman.GetColumn(ds_list_sman.row, "SMAN_CD") // 담당자코드
			  , ds_list_sman.GetColumn(ds_list_sman.row, "SMAN_NM") // 담당자명
			  , ds_list_sman.GetColumn(ds_list_sman.row, "SMAN_MAIL") // 담당자메일
			  ];
	close(arr); 	// 배열을 연동화면으로 전송하고 팝업화면을 닫는다.	
}

/********************************************************************************
// 취소버튼 클릭 시
