//=========================================================================================
// [ PART 2 ]
// Form Loading / Unloading 시 작업 정의
//=========================================================================================
function fn_OnLoadCompleted(obj) {
	// Form 로드 이벤트 맨 첫줄에 기술 
	gfn_initForm(true);

	fn_init_form(); 	// 폼 초기화

	// 조회
	fn_query();
}

/********************************************************************************
* 기      능   : form UnloadCompleted 
********************************************************************************/
function fn_OnUnloadCompleted(obj) {
	return gfn_isFormUnload(obj);
}

/********************************************************************************
* 기      능   : 폼 초기화
********************************************************************************/
function fn_init_form() {

 	ds_cond.ClearData(); 	
	ds_cond.AddRow();


	cal_reqdt_fm.Text = addDate(gfn_getCurrDate(), -365);	// 1년
	cal_reqdt_to.Text = gfn_getCurrDate(); 	

 	cb_reqgbn.Index = 0;
 	cb_statcd.Index = 0;
 	cb_goodsgbn.Index = 0;
 	cb_testgbn.Index = 0;	

 	// 의뢰자
 	switch (G_VEND_WGBN) {
		case "E" : 
			ed_reqnmid.Text = G_SAP_USER_PMNM_B;
			break;
		default  : 
			ed_reqnmid.Text = G_SAP_USER_NAME;
			break;
	}

	// 삭제 권한 2021.02.23 - 품질팀 이효재 ( 2026589 ) , 정보기술부 송광용( 2029847 ) , 송영호 ( 2021064 ) 
	//if( G_USER_ID != "TEST07" && G_USER_ID != "2026589") {
	if( G_USER_ID != "2026589" && G_USER_ID != "2029847" && G_USER_ID != "2021064") {
		btn_delete.Enable = false;	
	}	
}


//=========================================================================================
// [ PART 3 ]
// Main Process Event 정의(조회,입력,삭제,저장,인쇄,form닫기)
//=========================================================================================
/********************************************************************************
* 기      능   : 조회 1
********************************************************************************/
var fn_query_chk = false; //중복조회 방지용 flag

function fn_query() {

	fn_createWait("",this);     // 처리중입니다. 메시지 처리

	ds_error.ClearData();

	tit_clearActionInfo();
	tit_addSearchActionInfo("qm10:QM1001002A_S1");

	tit_callService(
          ""
        , ""
        , "ds_cond=ds_cond"
        , "ds_list=ds_list"
        , ""
        , "fn_afterQuery"
        , true);

}


/********************************************************************************
* 기      능   : 닫기  
********************************************************************************/
function fn_close() {
	close();
}

//=========================================================================================
// [ PART 4]
// Callback 함수 정의 : callback 함수명은 메인 함수의 fn_xxxx() 에서 xxxx 부분은 fn_afterXXXX()
// 형태가 되도록 작성 
//=========================================================================================
/********************************************************************************
* 기      능   : 조회 Callback  
********************************************************************************/
function fn_afterQuery(svcId, errCode, errMsg) {

	fn_query_chk = false; //중복조회 방지용 flag
	fn_destroyWait();
	if(ds_error.rowcount() > 0) 	{
		gfn_showAlert("CE00001");
		return;
	}

	gfn_showMsg("CI00002", ds_list.RowCount() + "");
}


//=========================================================================================
//[ PART 6 ]
//component Event 정의 : 각 Component 별 이벤트 
//=========================================================================================
/********************************************************************************
* 기      능   : 그리드 헤더 클릭시 정렬 처리 
********************************************************************************/
function grd_list_OnHeadClick(obj,nCell,nX,nY,nPivotIndex) {
	if ( nCell == 0 ) {
		return;
	}

	// 그리드 정렬 처리 
	gfn_sortGrid(obj,nCell);
}


/******************************************************
// 상세 조회 popup처리
******************************************************/
function grd_list_OnCellDblClick(obj,nRow,nCell,nX,nY,nPivotIndex)
{
	if (ds_list.RowCount() == 0)
		return;

	var formgbn = trim( ds_list.getColumn(nRow, "FORMGBN"));
	if (formgbn == null || formgbn == "")
	{
		gfn_showAlert("CW10046");
		return false;
	}

	// 2020.11.27 경고 문구 -> 접수 조회 화면으로 변경
	var statcd = trim( ds_list.getColumn(nRow, "STATCD") );
	var reqid  = trim( ds_list.getColumn(nRow, "REQID") );	// 의뢰자ID
	var creid  = trim( ds_list.getColumn(nRow, "CREID") );	// 생성자ID
	var rst    = trim( ds_list.getColumn(nRow, "RST") ); //시험결과(완료건)

	//if( statcd != "99" && reqid != G_USER_ID ) {
	if( reqid != G_USER_ID && creid != G_USER_ID && G_USER_ID != 'TEST07') {
		//gfn_showAlert("재의뢰는 완료된 건만 가능합니다. (본인 신청분 한해서 진행상태 열람 가능.)");
		gfn_showAlert("본인 의뢰건만 열람이 가능합니다.");
		return;
	}

	/*
	if (!(statcd == "99")) {
		gfn_showAlert("재의뢰는 완료된 건만 가능합니다.");
		return false;			
	}
	*/

	ds_arg.ClearData(); 
	var addRow = ds_arg.addRow();

	var vArg = "p_reqseq= " + ds_list.GetColumn(nRow, "REQSEQ")
		     + " p_auth= R";

	var url = "";

	switch(formgbn) 
	{
		case "1": // Lab.		
		case "4": // 정밀측정실
			if (statcd == "00" ) {
				url = "QM10::QM1001002H.xml";
			} else if (statcd == "99" && rst == "F" ) {
				// 불합격일 경우 차수증가 의뢰
				url = "QM10::QM1001002H.xml";				 
			} else {
				url = "QM10::QM1001003H.xml";
			}
			break;
		case "2": // Tower		
			if (statcd == "00") {
				url = "QM10::QM1001002I.xml";
			} else if (statcd == "99" && rst == "F" ) {
				// 불합격일 경우 차수증가 의뢰
				url = "QM10::QM1001002I.xml";				 
			} else {
				url = "QM10::QM1001003I.xml";
			}	

			break;		
		case "3": // Pilot		
			if (statcd == "00") {
				url = "QM10::QM1001002J.xml";
			} else if (statcd == "99" && rst == "F" ) {
				// 불합격일 경우 차수증가 의뢰
				url = "QM10::QM1001002J.xml";
			} else {
				url = "QM10::QM1001003J.xml";
			}
			break;
	}

	//var arr = gfn_openPopUpUrlHdel(url, true, vArg);
	var arr = Dialog(url, vArg, 1, 1, "TitleBar=false StatusBar=false", -1, Global.Window.Top+8);

	fn_query();
}


