/********************************************************************************
* 기      능   : 삭제
********************************************************************************/
function fn_delete() {	

	//if(!gfn_showConfirm( "CI00015", ds_list.getColumn(ds_list.currow, "TITLE") + " 시험 의뢰" )) return;
	if(!gfn_showConfirm( "CI00015", ds_list.getColumn(ds_list.currow, "TITLE"))) return;

	ds_cond.SetColumn(ds_cond.currow , "REQSEQ", ds_list.getColumn(ds_list.currow, "REQSEQ"));

	tit_clearActionInfo();
	tit_addSingleActionInfo("qm10:QM1001002A_D1");
	tit_addSingleActionInfo("qm10:QM1001002A_D2");
	tit_addSingleActionInfo("qm10:QM1001002A_D3");
	tit_addSingleActionInfo("qm10:QM1001002A_D4");
	tit_addSingleActionInfo("qm10:QM1001002A_D5");

	tit_callService(
        ""
        , ""
        , "ds_cond=ds_cond"
        , ""
        , ""
        , "fn_afterDelete"
		, ""
		, ""
        , true);    	
}


