/********************************************************************************
* 기      능   : Null check
********************************************************************************/
function fn_chkVal()
{
	var dscnt = ds_master.GetColCount();

	// 마스터 테이블
	for ( var i=0 ; i < dscnt ; i++ ) {

		var val = ds_master_chk.GetColumn(0, ds_master.GetColID(i));

		if( val != null && val != '0' && Length(Trim(ds_master.getColumn(0, ds_master.GetColID(i)))) == 0 ) {
			gfn_showAlert("CI30001", val + " 항목은 필수 항목입니다.");
			return false;		
		}	
	}

	// 시험 정보 	
	var testcnt = ds_test.rowcount;

	if ( testcnt < 1 ) { 
		gfn_showAlert("CI30001", "시험항목을 입력해 주세요." );			
		return false;
	}

	for ( var i=0 ; i < testcnt ; i++) {	
		// 4,5,6 컬럼 입력항목		
		if( Length(Trim(ds_test.getColumn(i, "TESTNM"))) == 0 ) {
			gfn_showAlert("CI30001", "시험정보 " + (i+1) + "번째 행에 입력되지 않은 값이 있습니다.");
			return false;		
		}		
		if( Length(Trim(ds_test.getColumn(i, "TESTCONT1"))) == 0 ) {
			gfn_showAlert("CI30001", "시험정보 " + (i+1) + "번째 행에 입력되지 않은 값이 있습니다.");
			return false;		
		}		
		if( Length(Trim(ds_test.getColumn(i, "TESTCONT2"))) == 0 ) {
			gfn_showAlert("CI30001", "시험정보 " + (i+1) + "번째 행에 입력되지 않은 값이 있습니다.");
			return false;
		}		
	}

	// 개발 항목
	var rndcnt = ds_rnd.rowcount;

	if ( rndcnt < 1 ) { 
		gfn_showAlert("CI30001", "개발항목을 입력해 주세요." );
		return false;		
	}

	for ( var i=0 ; i < rndcnt ; i++) {	
		if( Length(Trim(ds_rnd.getColumn(i, "GOODSNM"))) == 0 
			|| Length(Trim(ds_rnd.getColumn(i, "MODELNM"))) == 0 
			|| Length(Trim(ds_rnd.getColumn(i, "BFMATNR"))) == 0 
			|| Length(Trim(ds_rnd.getColumn(i, "RNDCONT"))) == 0 
			|| Length(Trim(ds_rnd.getColumn(i, "FILENM"))) == 0 ) {
			gfn_showAlert("CI30001", "개발항목 " + (i+1) + "번째 행에 입력되지 않은 값이 있습니다.");
			return false;		
		}
	}

	// 첨부파일
	var filecnt = ds_file.rowcount;

	/*
	11. 27 첨부파일 val check 제외
	if ( filecnt < 1 ) { 
		gfn_showAlert("CI30001", "파일을 첨부해 주세요." );
		return false;		
	}
	*/

	// 차수 증가 의뢰일 경우 개선대책보고서 필수
	var degree = toNumber(trim(ds_master.GetColumn(ds_master.row, "DEGREE"))) ;
	if( degree != null && degree > 1 ) {

		if( Length(Trim(ds_master.getColumn(ds_master.row, "IMP_FILENM"))) == 0 ) {

			gfn_showAlert("CI30001", " 개선대책보고서를 확인해 주세요. " );
			div_imp.ed_imp_filename.SetFocus();
			return false;		
		}		
	}	

	return true;
}


function btn_rndadd_OnClick(obj)
{
	var nRow = ds_rnd.AddRow();

	var nSeq = toNumber(ds_rnd.Max("RNDSEQ"));
	if ( nSeq == "" || nSeq == null ) nSeq = 0;

	ds_rnd.SetColumn(nRow, "RNDSEQ", nSeq+1);
	ds_rnd.setColumn(nRow, "FLAG"   , "I");
}

function btn_rnddel_OnClick(obj)
{
	//ds_rnd.DeleteRow(ds_rnd.row);
	//modFlg = true;
	var nRow = ds_rnd.currow;	

	if ( nRow > -1 ) {	
		var sRowType = ds_rnd.GetRowType(nRow);
		if ( sRowType == "insert" ) {		

			var sFileName = ds_file.GetColumn(nRow, "FILENM");

			if(length(sFileName) != 0) {
				var arr = tit_fileDelete("QM",ds_rnd.getColumn(nRow, "FILENM_APP"), false);
			}

			ds_rnd.DeleteRow(nRow);
			modFlg = true;

		} else {
			// 기존 파일은 저장시 삭제 처리
			ds_rnd_del.AddRow();	
			for(var i=0; i < ds_rnd.colcount; i++) { 
				ds_rnd_del.SetColumn( ds_rnd_del.currow
									, ds_rnd.GetColID(i)
									, ds_rnd.GetColumn(ds_rnd.currow, ds_rnd.GetColID(i)));
			}

			ds_rnd.DeleteRow(nRow);
			modFlg = true;
		}
	}	
}

