// Main Process Event 정의(조회,입력,삭제,저장,인쇄,form닫기)
//=========================================================================================
/********************************************************************************
* 기      능   : 폼 초기화
********************************************************************************/
function fn_init_form() {
	div_vbvg.Border = "none";
	//div_vbvg.cb_frvb.Enable 	= false;					//부서코드
	//div_vbvg.cb_frvg.Enable 	= false;					//팀코드

	cb_Ktokd.Index  = 0;
	div_Ktokd.SetFocus();
} 

/********************************************************************************
* 기      능   : 조회 1
********************************************************************************/
var fn_query_chk = false; //중복조회 방지용 flag

function fn_query() {

	ds_list.Clear();
	ds_error.Clear();

	if (fn_valid_chk("query") == false)  return;

	if(!fn_query_chk) {
		fn_query_chk = true;
	} else {
		gfn_showAlert("CW10025", "조회중입니다..");
		return;
	}	

	cmdFlag = "DISP";

	var cdKtokd = trim(cb_Ktokd.Value);								//고객구분					
	var cdKunnr = div_Ktokd.ed_buyr_cd.text;						//고객번호
	var cdStcd2 = ed_txtStcd2.Text;
	var cdStcd1 = ed_txtStcd1.Text;
	var name1   = ed_name.Text;
	var cmd     = cmdFlag;	

	ds_cond.ClearData();
	ds_cond.AddRow();

	ds_cond.SetColumn(0, "KTOKD", cdKtokd);
	ds_cond.SetColumn(0, "KUNNR", cdKunnr);
	ds_cond.SetColumn(0, "STCD2", cdStcd2);
	ds_cond.SetColumn(0, "STCD1", cdStcd1);	
	ds_cond.SetColumn(0, "NAME1", name1);
	ds_cond.SetColumn(0, "CMD", cmd);	

	// 서비스 호출
	nsfRequest("query"									// srvId
				, "Sbi0010/find"						// url
				, "ds_cond=ds_cond"						// inDsList
				, "ds_list=ds_list ds_error=ds_error"	// outDsList
				, ""									// strParam
				, "fn_afterQuery"						// CallBackFunc
				);

    fn_createWait("",this);     // 처리중입니다. 메시지 처리
}

/********************************************************************************
* 기      능   : 저장 
********************************************************************************/
function fn_save() 
{
	ds_error_save.Clear();

	vbvgDataBinding();

	if(ds_list.GetUpdate() == false) {
		 gfn_showAlert( "CW00005" );
		 return false;
    } 

    if(fn_save_data_chk() == false) {
		return false; 
    }

	if (!gfn_showConfirm("CI00008")) {	// 저장하시겠습니까?
		return;
	}

/*	// 2017.10.10 - 신규(INST)/변경(MODF) 분기 추가 by mj.Shin 
	if (length(ed_kunnr.Text) == 0){
		cmdFlag = "INST";
	} else {
		cmdFlag = "MODF";	
	}
	// RFC의 BDC 오류 보완 예정 
*/
	// 서비스 호출
	nsfRequest("update"									// srvId
				, "Sbi0010/save"						// url
				, "ds_list=ds_list"						// inDsList
				, "ds_error_save=ds_error"					// outDsList
				, "cmd=" + cmdFlag + ""					// strParam
				, "fn_afterSave"						// CallBackFunc
				);

    fn_createWait("",this);     // 처리중입니다. 메시지 처리
}

/********************************************************************************
* 기      능   : 닫기  
********************************************************************************/
function fn_close() {
	close();
}

//=========================================================================================
// [ PART 4]
// Callback 함수 정의 : callback 함수명은 메인 함수의 fn_xxxx() 에서 xxxx 부분은 fn_afterXXXX()
// 형태가 되도록 작성 
//=========================================================================================
/********************************************************************************
* 기      능   : 조회 Callback  
********************************************************************************/
function fn_afterQuery(svcId, errCode, errMsg) { 

	fn_query_chk = false; //중복조회 방지용 flag
	fn_destroyWait();

	//trace ("@@@@@@ "+ds_list.SaveXML());

	if ( ds_error.rowcount > 0 )	{	//  rpc error의 경우
		// SAP에서 처리된 메세지를 alert로 표시 후 이후 진행
		gfn_showAlert(ds_error.GetColumn(0, "ERRMSG"));
	}

	if(svcId == "query") {
		// 조회된 자료가 있을 경우 
		if (ds_list.RowCount() > 0) 
		{ 
		    gfn_showMsg("CI00002", ds_list.RowCount()+"");  // CI00002 : "조회를 완료했습니다.조회건수 [${}건]"
			cb_Ktokd1.Enable = false;			// 자료가 존재하는 경우 변경 불가
			if ( cb_Ktokd1.Value == "Z101" ) {
				ed_waers.Text = "";
				ed_waers.Enable = false;
			} else {
				ed_waers.Enable = true;
			}

			trace("ed_waers.Enable :    "+ed_waers.Enable);
			trace("cb_Ktokd1.Value :    "+cb_Ktokd1.Value);
		} 
		else 
		{
			gfn_showMsg("CW00010"); 					// CW00010 : "데이터가 없습니다."
			// 정보가 존재하지 않는 경우 처리
			ds_list.AddRow();
			cb_Ktokd1.Enable = true;
			cb_Ktokd1.Value = cb_Ktokd.Value;		// 고객유형

			cb_Ktokd1_Value_Change();

			ed_name1.Text   = ed_name.Text;			// 고객명
			ed_stcd2.Text   = ed_txtStcd2.Text;		// 사업자번호
			ed_stcd1.Text   = ed_txtStcd1.Text;		// 주민번호
			cmdFlag = "INST";						// 입력처리 flag
			Div2.rd_vtweg.Index = -1;				// 유통경로 초기화
		}
	}

	vbvgDataGetBinding();	
}

function vbvgDataGetBinding() 
{
	div_vbvg.cb_frvb.value = ds_list.GetColumn(0, "VKBUR");
	div_vbvg.cb_frvg.value = ds_list.GetColumn(0, "VKGRP"); 
}

// 항목 오류 검증
// pProcGb : 처리구분 (query:조회)
function fn_valid_chk(pProcGb)
{
	var custType  = cb_Ktokd.Value;
	var cutTypeNm = cb_Ktokd.Text;

	switch( custType )
	{
		case "Z001"	:	// 국내일반
			if (length(ed_txtStcd2.text) == 0)
			{
				gfn_showAlert("CW00208", cutTypeNm + "의 사업자번호" );
				gfn_SetFocus(ed_txtStcd2, "Y"); 
				return false;
			}
			break;
		case "Z002"	:	// 개인
			if (length(ed_txtStcd1.text) == 0)
			{
				gfn_showAlert("CW00208", cutTypeNm + "의 주민번호" );
				gfn_SetFocus(ed_txtStcd1, "Y"); 
				return false;
			}
			break;
		case "Z004"	:	// 해외
			if (length(ed_name.text) == 0)
			{
				gfn_showAlert("CW00208", cutTypeNm + "의 고객명" );
				gfn_SetFocus(ed_name, "Y"); 
				return false;
			}
			break;
		case "Z101"	:	// 현장
			if (length(ed_name.text) == 0)
			{
				gfn_showAlert("CW00208", cutTypeNm + "의 고객명" );
				gfn_SetFocus(ed_name, "Y"); 
				return false;
			}
			break;
		default :
			gfn_showAlert("CW00208", "고객유형" );
			gfn_SetFocus(cb_Ktokd,"Y");
			return false;
			break;
	}

	return true;
}

