				gfn_showAlert("CW00208", "세금분류");
				gfn_SetFocus(cb_taxkd,"Y"); 
				return false;
			}
			if (length(Div2.rd_vtweg.Value) == 0)
			{
				gfn_showAlert("CW00208", "유통경로" );
				gfn_SetFocus(Div2.rd_vtweg,"Y"); 
				return false;
			}
			break;

		case "Z101"	:	// 현장
			// 기본정보
			if (length(Div0.ed_ort01.Text) == 0)
			{
				gfn_showAlert("CW00208", "주소1" );
				gfn_SetFocus(Div0.ed_ort01,"Y"); 
				return false;
			}
			if (length(Div0.ed_stras.Text) == 0)
			{
				gfn_showAlert("CW00208", "주소2" );
				gfn_SetFocus(Div0.ed_stras,"Y"); 
				return false;
			}
			if (length(Div0.ed_pstlz.Text) == 0 && trim(Div0.ed_land1.Text) = "KR" )
			{
				gfn_showAlert("CW00208", "우편번호" );
				gfn_SetFocus(Div0.ed_pstlz,"Y"); 
				return false;
			}
			if (length(Div0.ed_pstlz.Text) == 0 && trim(Div0.ed_land1.Text) != "KR" )
			{
				Div0.ed_pstlz.text = ""; 
			}			
			if (length(Div0.ed_land1.Text) == 0) 
			{
				gfn_showAlert("CW00208", "국가" );
				gfn_SetFocus(Div0.ed_land1,"Y"); 
				return false;
			}
			if (length(Div0.ed_telf1.Text) == 0 && trim(Div0.ed_land1.Text) = "KR" )
			{
				gfn_showAlert("CW00208", "전화번호" );
				gfn_SetFocus(Div0.ed_telf1,"Y"); 
				return false;
			}
			if (length(cb_regio.Value) == 0)
			{
				gfn_showAlert("CW00208", "지역" );
				gfn_SetFocus(cb_regio,"Y"); 
				return false;
			}
			if (length(ed_lzone.Value) == 0)
			{
				gfn_showAlert("CW00208", "운송지역" );
				gfn_SetFocus(ed_lzone,"Y"); 
				return false;
			}
			// 영업영역
			if (length(div_vbvg.cb_frvb.Value) == 0)
			{
				gfn_showAlert("CW00208", "부서" );
				gfn_SetFocus(div_vbvg.cb_frvb,"Y"); 
				return false;
			}

/* ----< 2017.10.10 - 납품처 통화단위 입력 제외 by mj.Shin 
			if (length(ed_waers.Text) == 0)
			{
				gfn_showAlert("CW00208", "거래통화" );
				gfn_SetFocus(ed_waers,"Y"); 
				return false;
			}
// -----> 2017.10.10 - 납품처 통화단위 입력 제외 by mj.Shin  */

			if (length(cb_taxkd.Value) == 0)
			{
				gfn_showAlert("CW00208", "세금분류");
				gfn_SetFocus(cb_taxkd,"Y"); 
				return false;
			}
			break;
	}

	return true;
}

/********************************************************************************
