/********************************************************************************
* 기      능   : 저장 
********************************************************************************/
function vbvgDataBinding() 
{
	ds_list.SetColumn(0, "VKBUR", div_vbvg.cb_frvb.value);
	ds_list.SetColumn(0, "VKGRP", div_vbvg.cb_frvg.value); 
}

/********************************************************************************
* 기      능   : 저장 Callback  
********************************************************************************/
function fn_afterSave(svcId, errCode, errMsg) 
{
	fn_destroyWait();

	if (ds_error_save.rowcount > 0)	{
		gfn_showAlert(ds_error_save.GetColumn(0, "ERRMSG") );
		return;
	}else	{
		//  신규등록 후 재조회 Call (하단의 정보를 재조회를 위하여 조건 조건에 Mapping)
		if ( cmdFlag == "INST")	{
			ed_name.Text     = ed_name1.Text;
			ed_txtStcd2.Text = ed_stcd2.Text;
			ed_txtStcd1.Text = ed_stcd1.Text;
			cb_Ktokd.Value   = cb_Ktokd1.Value;
		}
		fn_query();
	}
}

