		ed_eml2.Enable 				= false;		 		//담당자 이메일
	}

	//기본값 설정
	if (cb_Ktokd1.Value == "Z001") {
		ed_waers = "KRW";
		cb_taxkd.Value = "1";
	} else if (cb_Ktokd1.Value == "Z002") {
		ed_waers = "KRW";
		cb_taxkd.Value = "2";
	} else if (cb_Ktokd1.Value == "Z004") {
		ed_waers = "USD";
		cb_taxkd.Value = "0";
	} else if (cb_Ktokd1.Value == "Z101") {
		//ed_waers = "KRW";					// 2017.10.10 - 납품처 통화단위 기본값 제외 by mj.Shin 
		cb_taxkd.Value = "1";
	}
}

