function btn_Pstlz_popup(obj)
{
	if (Obj.id == "btn_pstlz") {
		var arg = "strObj="+quote(Obj.id);       
		var arr = gfn_openPopUpUrlHdel("SES::Ses0034.xml", true, arg);	
		if (arr != null && arr != 0)
		{
			Div0.ed_pstlz.text = substr(arr[0],0,3) + "-" + substr(arr[0],3,3) ;
			Div0.ed_ort01.text = arr[1];
			Div0.ed_stras.text = "";
			Div0.ed_stras.SetFocus();

		}
	} else {
		var arg = "strObj="+quote(Obj.id);
		strZipcodeObj=Obj.id;
		AxMSIE0.Navigate2(G_SERVER_URL + "addressSearchLink.jsp");	
	}

}

