********************************************************************************/
function cb_Ktokd_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
	// '현장'일 때 건축물 인허가 정보 버튼 활성화되도록
	if(obj.Text == "현장"){
		btn_Bdlic.Visible = true;
	} else if(btn_Bdlic.Visible == true) {
		btn_Bdlic.Visible = false;
	}
}

