function fn_OnLoadCompleted(obj) 
{
	// 리사이징
	fn_FormResize(true);	

	// 계획년도 콤보 데이타 Load 및 기본값 SETTING 
	var curYear = substr(gfn_getCurrDate(),0,4); 
	fn_Year(curYear,-5,5,1);

	// 제품군 ( componentId, comboGubun, DatasetId, 전체여부 )
	fn_SetListCode(cb_spart, "spart", "ds_product", "Y");	
	// 조회구분 ( componentId, comboGubun, DatasetId, 전체여부 )
	fn_SetListCode(cb_where, "whereGubun", "ds_where", "N");

	// 제품 ( componentId, comboGubun, DatasetId, 전체여부, 그리드/콤보여부 )
	fn_SetListCode( 12, "spart", "ds_spart", "N", "grid");
	// 자재번호 ( componentId, comboGubun, DatasetId, 전체여부, 그리드/콤보여부 )
	fn_SetListCode( 14, "matnr", "ds_matnr", "N", "grid");
	// 기종 ( componentId, comboGubun, DatasetId, 전체여부, 그리드/콤보여부 )
	fn_SetListCode( 19, "gtype", "ds_gtype", "N", "grid");
	// 설치지역 ( componentId, comboGubun, DatasetId, 전체여부, 그리드/콤보여부 )
	fn_SetListCode( 27, "region", "ds_region", "N", "grid");
	// 단납기 ( componentId, comboGubun, DatasetId, 전체여부, 그리드/콤보여부 )
	fn_SetListCode( 28, "zdeli", "ds_zdeli", "N", "grid");
	// 국내상해 ( componentId, comboGubun, DatasetId, 전체여부, 그리드/콤보여부 )
	fn_SetListCode( 29, "shangh", "ds_shangh", "N", "grid");
