/********************************************************************************
* 조회 
********************************************************************************/
var fn_query_chk = false; //중복 조회방지용 flag

function fn_query() { 

	// 그리드 설정
	fn_GridSet();

	// sort mark 삭제 
	gfn_clearGridSortMark(Grid); 

	ds_output.ClearData();  	

	// 필수 체크 - [계획년도]
	if ( length(cb_year.Value) == 0 )
	{
		gfn_showAlert("CW00002", "계획년도");	//"필수 입력항목입니다.\n확인하여 주십시오."
		cb_year.setFocus();
		return;
	}
	// 필수 체크 - [조회구분]
	if ( length(cb_where.Value) == 0 ) 
	{
		gfn_showAlert("CW00002", "조회구분");	//"필수 입력항목입니다.\n확인하여 주십시오."
		cb_where.setFocus();
		return;
	}

	if(!fn_query_chk) {
		fn_query_chk = true;
	} else {
		gfn_showAlert("CW10025", "조회중입니다..");
		return;
	}

	ds_input.SetColumn(0, "ZPYEAR",  cb_year.Value); 				// 계획년도	
	ds_input.SetColumn(0, "WHERE", 	 cb_where.Value);				// 조회구분
	ds_input.SetColumn(0, "SPART",	 cb_spart.Value);				// 제품군
	ds_input.SetColumn(0, "VKBUR_F", div_vbvg.cb_frvb.value); 		// 사업장 from
	ds_input.SetColumn(0, "VKBUR_T", div_vbvg.cb_tovb.value); 		// 사업장 to
	ds_input.SetColumn(0, "VKGRP_F", div_vbvg.cb_frvg.value); 		// 영업그룹 from 
	ds_input.SetColumn(0, "VKGRP_T", div_vbvg.cb_tovg.value); 		// 영업그룹 to
	ds_input.SetColumn(0, "ZKUNNR",  div_manager.ed_sman_cd.text);	// 영업사원

	// 조회구분(1:수주,2:매출,3:청구,4:수금)
	if ( ds_input.GetColumn(0, "WHERE") == '1' )
	{
		Grid.SetColProp(7,"width",0);
		Grid.SetCellProp('Head', 28,"text","수주예상액");
	}
	else if ( ds_input.GetColumn(0, "WHERE") == '2' )
	{
		Grid.SetColProp(7, "width", 56);
		Grid.SetCellProp('Head', 7, "text", "매출년월");
		Grid.SetCellProp('Head', 28,"text","매출예상액");
	}
	else if ( ds_input.GetColumn(0, "WHERE") == '3' ) 
	{
		Grid.SetColProp(7, "width", 56);
		Grid.SetCellProp('Head', 7, "text", "청구년월");
		Grid.SetCellProp('Head', 28,"text","청구예상액");
	}
	else if ( ds_input.GetColumn(0, "WHERE") == '4' ) 
	{
		Grid.SetColProp(7, "width", 56);
		Grid.SetCellProp('Head', 7, "text", "수금년월");
		Grid.SetCellProp('Head', 28,"text","수금예상액");
	}

	fn_createWait("",this);     // 처리중입니다. 메시지 처리

	nsfRequest("query"					// srvId
				, "sbp0090/find"			// url
				, "ds_input=ds_input ds_output=ds_output"		// biz에서 받는 명, mip에서 보내는 명
				, "ds_output=ds_output"		// mip에서 받는 명, biz에서 보내는 명
				, "exchg_usdkrw=exchg_usdkrw"// strParam
				, "fn_afterQuery"						// CallBackFunc
				);

}

/********************************************************************************
* 기      능   : 조회 Callback  
********************************************************************************/
function fn_afterQuery(strSvcID, nErrorCode, strErrorMag)
{ 
	if ( nErrorCode < 0 )
	{
		fn_query_chk = false; //중복조회 방지용 flag
		fn_destroyWait();
		gfn_showMsg("CE00001");	//  CE00001 : "처리에 실패했습니다.\n확인 후 다시 처리해 주십시오.");
	}
	else
	{
		if ( strSvcID == "query" )
		{	
			if ( ds_output.GetRowCount() > 0 )
			{				
				applyBPExchgRates();
				gfn_showMsg("CI00002", ds_output.RowCount() + ""); 	// CI00002 : "조회를 완료했습니다.조회건수 [${}건]"

			}
			else
			{
				gfn_showMsg("CW00010"); 			// CW00010 : "데이터가 없습니다."
				Grid.NoDataText = gridNoDataMsg;	// 그리드에 표시 - 조회된 데이터가 없습니다.
			}
			fn_query_chk = false; //중복조회 방지용 flag
			fn_destroyWait();
		}
		// 코드성
		else if ( strSvcID == "code" )
		{
		}
	}


}
function applyBPExchgRates() {
	var convertedKRW=0;
	for(var i=0; i < ds_output.GetRowCount(); i++) {
		switch(ds_output.GetColumn(i,'WAERK')) {
			case 'KRW':
				convertedKRW = ds_output.GetColumn(i,'SOFOCA');
				break;

			case 'USD':
				convertedKRW = toNumber(ds_output.GetColumn(i,'SOFOCA')) * toNumber(exchg_usdkrw);
				break;

			default:
		}
		ds_output.SetColumn(i,'SOFOCA2',convertedKRW);
	}
}
/******************************************************
// 그리드 설정
******************************************************/
function fn_GridSet()
{	

	http.Sync = true;

	Destroy("ds_team");
	Create("dataset", "ds_team");
	object("ds_team").AddColumn("CODE","STRING","256");
	object("ds_team").AddColumn("CODE_NAME","STRING","256");
	object("ds_team").AddColumn("FILTER1","STRING","256");

	callServiceN("code"
				, "com0060/teamCd"
				, "ds_input=ds_team"	// biz에서 받는 명, mip에서 보내는 명
				, "ds_team=ds_output"	// mip에서 받는 명, biz에서 보내는 명
				, ""
				, "");

	object("ds_team").insertRow(0);
	object("ds_team").setColumn(0, "CODE", "0");
	object("ds_team").setColumn(0, "CODE_NAME", "");
	object("ds_team").setColumn(0, "FILTER1", "0"); // 필터용으로 사용할 부서코드

	Grid.SetCellProp('Body', 9, "ComboDataset", "ds_team");

	// 동적으로 생성된 데이타셋을 반영하기 위해 redraw
	Grid.Redraw("Body");

	http.Sync = false;
}

/******************************************************
* 그리드 헤더 클릭시 정렬 처리 
*******************************************************/
function grd_list_OnHeadClick(obj,nCell,nX,nY,nPivotIndex) 
{
	if (nCell == 0) 
	{
		// 컬럼 display 여부
		gfn_selectGridCol(obj); 
	} 
	else 
	{
		// 그리드 정렬 처리 
		gfn_sortGrid(obj, nCell);
	}

}

/******************************************************
* 엑셀로 export
*******************************************************/
function fn_excel()
{
	gfn_exportExcel(Grid, "사업계획 현황");
}


function fn_query_bdtyp() {
	// 건물용도 콤보목록 조회
	nsfRequest("query"									// srvId
				, "com0160/find"						// url
				, "ds_cond_build=ds_cond_build ds_list_build=ds_list_build"		// inDsList
				, "ds_list_build=ds_list_build ds_error_build=ds_error_build"	// outDsList
				, ""									// strParam
				, "fn_afterQuery_bdtyp"						// CallBackFunc
				);  
}

// 건물용도 코드목록 조회 CALL BACK함수
