function fn_OnLoadCompleted(obj) {
	// Form 로드 이벤트 맨 첫줄에 기술 
	gfn_initForm(true);

	gfn_translation(obj, G_LANG);

	fn_init_form(); 	// 폼 초기화
	if (G_USER_GROUP_R == 'R10') {
	// G_VEND_CODE 협력사
	} else if (G_USER_GROUP_R == 'R20') {
	// G_VEND_CODE 보수PM
	// G_VEND_CODE 협력사
	} else if (G_USER_GROUP_R == 'R30') {
	} else if (G_USER_GROUP_R == 'R40') {
	alert(G_USER_GROUP+'\t'+G_USER_GROUP_R+'\t'+G_VEND_CODE+'\t'+G_USER_ID+'\t'+G_AREA_CODE);
	}
}

/********************************************************************************
* 기      능   : 폼 초기화
********************************************************************************/
function fn_init_form() {
	ds_cond.DeleteAll();
	ds_cond.AddRow();

	var curYear = substr(gfn_getCurrDate(),0,4);
	fn_makeYear(curYear,-4,1);
	cb_fr_monthf.index = parseInt(substr(gfn_getCurrDate(),4,2))-1;
	cb_to_monthf.index = parseInt(substr(gfn_getCurrDate(),4,2))-1;

	// 오더유형 콤보목록 조회
	nsfRequest("query"						// srvId
				, "combo/comboAuart"		// url
				, "ds_auart_cd=ds_auart_cd"	// inDsList
				, "ds_auart_cd=ds_auart_cd"	// outDsList
				, ""						// strParam
				, "fn_afterAuart"			// CallBackFunc
				);

	// 수주결과 콤보목록 조회
	ds_sorlt.DeleteAll();
	ds_sorlt.AddRow();
	ds_sorlt.SetColumn(0,"CODE","SD111");
	ds_sorlt.SetColumn(0,"CODE_NAME","SD111");
	// 서비스 호출
	nsfRequest("query"					// srvId
				, "combo/comboXml"		// url
				, "ds_sorlt=ds_sorlt"	// inDsList
				, "ds_sorlt=ds_sorlt"	// outDsList
				, ""					// strParam
				, "fn_afterQuery_sorlt"	// CallBackFunc
				);

	// 수주가능성- SOABLE
	ds_combo.DeleteAll();
	ds_combo_gubun.DeleteAll();
	ds_combo_gubun.AddRow();
	ds_combo_gubun.SetColumn(0, "GUBUN", 'ds_cond_soable');
	ds_cond_soable.DeleteAll();
	ds_cond_soable.AddRow();
	ds_cond_soable.SetColumn(0, "DOMNAME", 'ZSOABLE');

	// 서비스 호출
	nsfRequest("SOABLE"																				// srvId
				, "sbp0190/combo"																	// url
				, "ds_combo_gubun=ds_combo_gubun ds_cond_soable=ds_cond_soable ds_combo=ds_combo"	// inDsList
				, "ds_combo=ds_combo"																// outDsList
				, ""																				// strParam
				, "fn_afterQuery_combo"																// CallBackFunc
				);

	// 기종- GTYPE
	ds_combo.DeleteAll();
	ds_combo_gubun.DeleteAll();
	ds_combo_gubun.AddRow();
	ds_combo_gubun.SetColumn(0, "GUBUN", 'ds_cond_gtype');

	// 서비스 호출
	nsfRequest("GTYPE"																	// srvId
				, "sbp0190/combo"														// url
				, "ds_combo_gubun=ds_combo_gubun ds_combo=ds_combo ds_gtypes=ds_gtypes"	// inDsList
				, "ds_combo=ds_combo ds_gtypes=ds_gtypes"								// outDsList
				, ""																	// strParam
				, "fn_afterQuery_combo"													// CallBackFunc
				);

	// 설치지역- REGION
	ds_combo.DeleteAll();
	ds_combo_gubun.DeleteAll();
	ds_combo_gubun.AddRow();
	ds_combo_gubun.SetColumn(0, "GUBUN", 'ds_cond_region');

	// 서비스 호출
	nsfRequest("REGION"													// srvId
				, "sbp0190/combo"										// url
				, "ds_combo_gubun=ds_combo_gubun ds_combo=ds_combo"		// inDsList
				, "ds_combo=ds_combo"									// outDsList
				, ""													// strParam
				, "fn_afterQuery_combo"									// CallBackFunc
				);

	ds_zprgflg.DeleteAll();
	ds_zprgflg.AddRow();
	ds_zprgflg.SetColumn(0,"CODE","SD108");
	ds_zprgflg.SetColumn(0,"CODE_NAME","SD108");
	// 서비스 호출
	nsfRequest("query"						// srvId
				, "combo/comboXml"			// url
				, "ds_zprgflg=ds_zprgflg"	// inDsList
				, "ds_zprgflg=ds_zprgflg"	// outDsList
				, ""						// strParam
				, "fn_afterQuery_zprgflg"	// CallBackFunc
				);

	ds_sbdcm.DeleteAll();
	ds_sbdcm.AddRow();
	ds_sbdcm.SetColumn(0,"CODE","BS106");
	ds_sbdcm.SetColumn(0,"CODE_NAME","BS106");

	// 서비스 호출
	nsfRequest("query"					// srvId
				, "combo/comboXml"		// url
				, "ds_sbdcm=ds_sbdcm"	// inDsList
				, "ds_sbdcm=ds_sbdcm"	// outDsList
				, ""					// strParam
				, "fn_afterQuery_sbdcm"	// CallBackFunc
				);

	div_vbvg.cb_big.left = 0;
	div_vbvg.cb_mid.left = 136;
	div_vbvg.cb_small.left = 272;
	div_vbvg.ed_sman_cd.left = 408;
	div_vbvg.btn_popSman.left = 523;
	div_vbvg.ed_sman_nm.Visible = false;

	gfn_insertEmptyRow(ds_spart_cd, G_CODE_ALL);
	cb_spart.index  = 0;

	cb_fr_yearf.SetFocus();
}

/********************************************************************************
* 기      능   : 콤보박스 조회 Callback  
********************************************************************************/
function fn_afterQuery_combo(svcId, errCode, errMsg) {

	switch(svcId) {
		case "SOABLE" :	// 수주가능성
			ds_soable.Copy(ds_combo);
			break;
		case "GTYPE" :	// 기종
			ds_gtype.Copy(ds_combo);
			break;
		case "REGION" :	// 설치지역
			ds_region.Copy(ds_combo);
			break;
	}
}

function fn_afterQuery_zprgflg(errCode, errMsg) 
{
}

/********************************************************************************
* 기      능   : 지급예정년 계산(현재년도,당해년도에서 뺄 년수, 당해년도에서 더할 년수
********************************************************************************/
function fn_makeYear(curYear,sNum,eNum) {
	var rowCnt = eNum-sNum+4;
	var row = 0;

	for(var i=rowCnt; i>-1; i--) {
		ds_yearf_cd.AddRow();
		ds_yearf_cd.SetColumn(row,"CODE",parseInt(curYear)+i+parseInt(sNum));
		ds_yearf_cd.SetColumn(row,"CODE_NAME",(parseInt(curYear)+i+parseInt(sNum))+"년");
		row++;
	}

	cb_fr_yearf.index = eNum+4;
	cb_to_yearf.index = eNum+4;
}

