/********************************************************************************
* 기      능   : 조회
********************************************************************************/
function fn_query() {
	F_MERGE = false;
	fn_param_query();

	// 서비스 호출
	nsfRequest("query"									// srvId
				, "sbp0190/find"						// url
				, "ds_cond=ds_cond ds_list=ds_list"		// inDsList
				, "ds_list=ds_list ds_error=ds_error"	// outDsList
				, ""									// strParam
				, "fn_afterQuery"						// CallBackFunc
				);

    fn_createWait("",this);     // 처리중입니다. 메시지 처리
}

/********************************************************************************
* 기      능   : 저장
********************************************************************************/
function fn_save() {
	if (F_MERGE) {
		fn_param_query();

		if (fn_valid_save() == false) return;

		ds_list.Fireevent = false;

		// 서비스 호출
		nsfRequest("query"									// srvId
					, "sbp0190/save"						// url
					, "ds_cond=ds_cond ds_list=ds_list"		// inDsList
					, "ds_list=ds_list ds_error=ds_error"	// outDsList
					, ""									// strParam
					, "fn_afterSave"						// CallBackFunc
					);

		fn_createWait("",this);     // 처리중입니다. 메시지 처리
	} else {
		Alert("수정/추가된 항목이 없습니다.");
	}
}

function fn_valid_save() {
	for (var nRow = 0; nRow < ds_list.RowCount() ; nRow++) {
		if (ds_list.GetColumn(nRow,'CHK') == '1') {
			if (length(ds_list.GetColumn(nRow,'GTYPE')) == 0 or ds_list.GetColumn(nRow,'GTYPE') == ' ') {
				alert('저장/수정할 항목의\n기종을 입력하세요.');
				return false;
			}
			if (ds_list.GetColumn(nRow,'VKBUR') != 'B500' && ds_list.GetColumn(nRow,'VKBUR') != 'B600' && 
				ds_list.GetColumn(nRow,'VKBUR') != 'B700' && ds_list.GetColumn(nRow,'VKBUR') != 'B900' && 
				ds_list.GetColumn(nRow,'VKBUR') != 'BA00' && ds_list.GetColumn(nRow,'VKBUR') != 'BB00') {
				if (length(ds_list.GetColumn(nRow,'REGION')) == 0 or ds_list.GetColumn(nRow,'REGION') == ' ') {
					alert((nRow+1) + '행에서 저장/수정할 항목의\설치지역을 입력하세요.');
					global.FRM_BOTTOM.st_msg.value 	= (nRow+1) + '행에서 저장/수정할 항목의\n설치지역을 입력하세요.';
					global.FRM_BOTTOM.st_msg.color 	= 'red';
					return false;
				}
			}
			if (length(ds_list.GetColumn(nRow,'KUNNR')) == 0 or ds_list.GetColumn(nRow,'KUNNR') == ' ') {
				alert('저장/수정할 항목의\n고객번호를 입력하세요.');
				return false;
			}
			if (length(ds_list.GetColumn(nRow,'GSNAM')) == 0 or ds_list.GetColumn(nRow,'GSNAM') == ' ') {
				alert('저장/수정할 항목의\n공사명을 입력하세요.');
				return false;
			}
			if (ds_list.GetColumn(nRow,'ZFORE') == 0 or ds_list.GetColumn(nRow,'ZFORE') == ' ') {
				alert('저장/수정할 항목의\n예상시행율을 입력하세요.');
				return false;
			}
			if (ds_list.GetColumn(nRow,'DELDAT') == '00000000' or ds_list.GetColumn(nRow,'DELDAT') == '00000101') {
				alert((nRow+1) + '행에서 저장/수정할 항목의\n납기일자를 입력하세요.');
				global.FRM_BOTTOM.st_msg.value 	= (nRow+1) + '행에서 저장/수정할 항목의\n납기일자를 입력하세요.';
				global.FRM_BOTTOM.st_msg.color 	= 'red';
				return false;
			}
		}
	}

	return true;
}

/********************************************************************************
* 기      능   : 종결처리
********************************************************************************/
function fn_sorlt() {
	var arg = "fa_name="+quote("종결처리")
			+ " fa_sonnr="+quote(ds_list.GetColumn(F_SORLTROW,'SONNR'))
			+ " fa_zpym="+quote(ds_list.GetColumn(F_SORLTROW,'ZPYM'))
			+ " fa_sortl="+quote(ds_list.GetColumn(F_SORLTROW,'SORLT'))
			+ " fa_zdeli="+quote(ds_list.GetColumn(F_SORLTROW,'ZDELI'))
			+ " fa_auart="+quote(ds_list.GetColumn(F_SORLTROW,'AUART'))
			;

	var arr = gfn_openPopUpUrlHdel("Sbp::Sbp0192.xml", true, arg);

	if (arr == 'ok')
	{
		fn_query();
	}
}

