* 기      능   : 견적등록
********************************************************************************/
function fn_ses0031() {
	ds_arg.DeleteAll();
	ds_arg.AddRow();
	ds_arg.CopyRow(0, ds_list, ds_delete.GetColumn(0,'ROW'));

	var auart = Trim(ds_list.GetColumn(grd_list.CurrentRow, "AUART"));

	if ( auart == "") {
		alert("판매문서 유형이 없습니다.");	return;
	} else {
		var arg = "flag="+quote("I");
		gfn_openPopUpUrlHdel("Ses::Ses0031.xml", true, arg);
	}
}

