/********************************************************************************
* 기      능   : 조회 변수 설정
********************************************************************************/
function fn_param_query() {
	var frzpym = SubStr(cb_fr_yearf.Text,0,cb_fr_yearf.Text.Length(cb_fr_yearf.Text)-1) + Lpad(SubStr(cb_fr_monthf.Text,0,cb_fr_monthf.Text.Length(cb_fr_monthf.Text)-1),"0",2);
	var tozpym = SubStr(cb_to_yearf.Text,0,cb_to_yearf.Text.Length(cb_to_yearf.Text)-1) + Lpad(SubStr(cb_to_monthf.Text,0,cb_to_monthf.Text.Length(cb_to_monthf.Text)-1),"0",2);
	var spart = ds_spart_cd.GetColumn(cb_spart.Index, "CODE");
	var zmpflg = chk_zmpflg.Value;

	ds_cond.DeleteAll();
	ds_cond.AddRow();

	if (G_USER_GROUP_R == 'R10') {
	} else if (G_USER_GROUP_R == 'R20') {
	} else if (G_USER_GROUP_R == 'R30') {
	} else if (G_USER_GROUP_R == 'R40') {
	}
	ds_cond.SetColumn(0, "FR_ZPYM", frzpym);
	ds_cond.SetColumn(0, "TO_ZPYM", tozpym);
	ds_cond.SetColumn(0, "ZPYM", substr(gfn_getCurrDate(),0,6));
	ds_cond.SetColumn(0, "SPART", spart);
	ds_cond.SetColumn(0, "ZKUNNR" , div_vbvg.ed_sman_cd.Text);
	ds_cond.SetColumn(0, "VKBUR", div_vbvg.cb_frvb.Value);
	ds_cond.SetColumn(0, "VKGRP", div_vbvg.cb_frvg.Value);
	ds_cond.SetColumn(0, "ZMPFLG", zmpflg);
	ds_cond.SetColumn(0, "SORLT", cb_sorlt.Value);
}

/********************************************************************************
* 기      능   : 로우 추가시 발리데이션 체크
********************************************************************************/
function fn_valid_plus() {
	if (length(trim(div_vbvg.ed_sman_cd.text))==0)
	{
		// CW10029=${}을/를 선택하세요.
		gfn_showAlert('CW10029','담당자');
		gfn_SetFocus(div_vbvg.ed_sman_cd,"Y"); 
		return false;
	}
	if (div_vbvg.cb_frvb.Index == 0 and div_vbvg.cb_frvb.Text == ' 전체 -') {
		// CW10029=${}을/를 선택하세요.
		gfn_showAlert('CW10029','부서');
		gfn_SetFocus(div_vbvg.cb_frvb,"Y"); 
		return false;
	}
	if (div_vbvg.cb_frvg.Index == 0 and div_vbvg.cb_frvg.Text == ' 전체 -') {
		// CW10029=${}을/를 선택하세요.
		gfn_showAlert('CW10029','팀');
		gfn_SetFocus(div_vbvg.cb_frvg,"Y"); 
		return false;
	}
}

/********************************************************************************
* 기      능   : 데이타셋 로우 추가
********************************************************************************/
function fn_plus() {
	// 저장항목 오류검증
	if (fn_valid_plus() == false)  return false;

	var spart = '';
	if (cb_spart.Index == 0) {
		spart = ' ';
	} else {
		spart = ds_spart_cd.GetColumn(cb_spart.Index, "CODE");
	}

	ds_list.Fireevent = false;
	ds_list.AddRow();
	ds_list.SetColumn(ds_list.RowCount()-1,'CHK','0');
